INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Leobog AMG65 TM (Trắng/Light feather switch) AMG6503', 'ban-phim-leobog-amg65-tm-trang-light-feather-switch-amg6503', 21,
        '{"Tên sản phẩm":"Leobog AMG65 TM Trắng Light feather switch","Kết nối":"Có dây Type-C / Không dây 2.4G / Bluetooth","Kích thước":"325.18 × 143.38 × 44.28 mm","Switch":"Light feather switch (Silent)","Layout":"75% (67 phím)","Trọng lượng":"Khoảng 1020g (không bao gồm dây/đầu thu)","Bảo hành":"24 tháng","Tính năng":"Hot-swap 5 pin, Gasket mount, Mạch xuôi, Màn Dot Matrix","Đèn LED":"RGB","Pin":"Lithium 8000mAh, Khoảng 40 giờ (hiệu ứng ánh sáng mặc định), khoảng 106 giờ (tắt LED)","Keycap":"PBT Double-Shot"}',
        '# LEOBOG 3 Mode AMG65 Trắng Light feather switch\n\n**LEOBOG 3 Mode AMG65 Trắng Light feather switch** là mẫu bàn phím cơ mang phong cách thiết kế **Tenkeyless** gọn gàng với **layout 67 phím**, lý tưởng cho những ai muốn tiết kiệm không gian nhưng vẫn đảm bảo đầy đủ chức năng sử dụng. Vỏ ngoài **màu trắng thanh lịch** kết hợp với **keycap PBT Double-Shot cao cấp**, không chỉ bền màu mà còn mang lại cảm giác gõ chắc chắn, không bị mòn sau thời gian dài sử dụng. **Case hợp kim nhôm** được gia công tỉ mỉ, giúp tổng thể gaming gear chắc chắn và sang trọng hơn hẳn so với các dòng bàn phím cùng phân khúc.\n\n### Hiệu năng vượt trội với switch cao cấp\n\nĐiểm nổi bật nhất của sản phẩm chính là việc trang bị **Light feather switch** với **độ bền lên tới 60 triệu lần bấm**, cho cảm giác nhấn mượt, hành trình phím hợp lý và đặc biệt hiệu ứng âm thanh **Silent**, mang lại trải nghiệm gõ êm ái, phù hợp cho môi trường làm việc yên tĩnh hoặc phòng học. Bàn phím hỗ trợ **hot-swap 5 pin**, cho phép người dùng dễ dàng thay đổi switch mà không cần hàn, mở rộng khả năng tùy biến cá nhân một cách nhanh chóng.\n\n### Hệ thống LED RGB sống động\n\nSản phẩm được trang bị **đèn nền LED RGB** với nhiều hiệu ứng ánh sáng đa dạng cho phép người dùng tùy chỉnh theo sở thích hoặc đồng bộ với các thiết bị gaming khác. Đặc biệt, điểm cộng lớn nằm ở **màn Dot Matrix tháo rời**, hiển thị các hiệu ứng LED độc đáo và **màn hình TFT 1,14 inch** cho phép hiển thị thông tin hệ thống hoặc hình ảnh tùy chỉnh, tăng tính cá nhân hóa và thẩm mỹ khi sử dụng.\n\n### Kết nối đa dạng, linh hoạt mọi tình huống\n\nBàn phím hỗ trợ **kết nối 3 mode** gồm: **có dây Type-C**, **không dây 2.4GHz** và **Bluetooth**, giúp dễ dàng kết nối với các thiết bị như máy tính, laptop, tablet hay điện thoại di động. Với công nghệ kết nối hiện đại, người dùng có thể linh hoạt chuyển đổi giữa các thiết bị chỉ trong vài giây, phù hợp cho cả công việc lẫn giải trí đa nền tảng.\n\n### Thời lượng pin ấn tượng và công năng tối ưu\n\nVới dung lượng pin lên đến **8000mAh**, **LEOBOG AMG65 Trắng Light feather** có thể hoạt động liên tục khoảng **40 giờ với hiệu ứng LED mặc định** hoặc lên tới **106 giờ khi tắt đèn**. **Công nghệ gasket mount** kết hợp với **mạch xuôi** giúp nâng cao độ êm ái và cảm giác đàn hồi khi gõ, phù hợp cho cả người dùng chuyên nghiệp lẫn người mới bắt đầu làm quen với bàn phím cơ.\n\n### Phụ kiện đầy đủ, hỗ trợ tối đa cho người dùng\n\nĐi kèm với bàn phím là **sách hướng dẫn sử dụng**, **dây cáp USB Type-C**, **dụng cụ thay switch**, và **switch tặng kèm (1–4 chiếc)** để người dùng có thể tùy chỉnh ngay khi cần. Đây là điểm cộng lớn giúp người dùng dễ dàng bảo trì hoặc nâng cấp thiết bị của mình ngay tại nhà mà không cần dụng cụ chuyên biệt.\n\n**LEOBOG 3 Mode AMG65 Trắng Light feather switch** là sự lựa chọn hoàn hảo cho những ai tìm kiếm một chiếc bàn phím cơ không chỉ đẹp, bền mà còn linh hoạt trong kết nối và mạnh mẽ về hiệu năng. Thiết kế tối giản nhưng đầy đủ tiện ích, chiếc bàn phím này sẽ là trợ thủ đắc lực cho cả công việc lẫn giải trí hằng ngày.',
        1, 'https://cdn.hstatic.net/products/200000722513/download__12__b1e43d8acc014e13b3ce4e20fafa49b9.png'
    );
