INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA WIN68HE MAX có dây (Đen + đỏ/ Wing Chun Magnetic switch - Rapid trigger) WIN68HE05', 'ban-phim-aula-win68he-max-co-day-den-do-wing-chun-magnetic-switch-rapid-trigger-win68he05', 22,
        '{"Tên sản phẩm": "AULA WIN68HE MAX", "Mã sản phẩm": "WIN68HE05", "Loại bàn phím": "Bàn phím cơ từ tính (Magnetic)", "Kiểu kết nối": "Có dây USB-C", "Layout": "68 phím", "Loại Switch": "Magnetic Wing Chun", "Tính năng Switch đặc biệt": "Rapid Trigger", "Màu sắc": "Đen - Đỏ", "Lực nhấn Switch": "30 gf", "Hành trình phím": "Điều chỉnh 0.1 - 4.0 mm", "Chất liệu Keycap": "PBT Double-shot", "Đèn nền": "RGB tùy chỉnh", "Tần số phản hồi": "1000 Hz", "Anti-ghosting": "N-Key Rollover", "Phần mềm điều khiển": "AULA Gaming Software", "Tương thích hệ điều hành": "Windows, macOS", "Kích thước": "320 x 110 x 30 mm", "Trọng lượng": "800 g"}',
        'Bàn phím AULA WIN68HE MAX sở hữu thiết kế mạnh mẽ với lớp vỏ màu đen kết hợp cùng những điểm nhấn đỏ đầy cá tính. Vật liệu chất lượng cao cùng kích thước nhỏ gọn giúp sản phẩm này không chỉ phù hợp với không gian gaming mà còn dễ dàng di chuyển.\n\nĐèn LED RGB ấn tượng\nMột trong những điểm nổi bật của chiếc bàn phím máy tính này chính là hệ thống đèn LED RGB đa dạng. Người dùng có thể tùy chỉnh màu sắc và hiệu ứng ánh sáng theo sở thích cá nhân, tạo nên không gian chơi game đầy màu sắc và sống động.\n\nCông nghệ Wing Chun Magnetic Switch\nBàn phím AULA WIN68HE MAX được trang bị công nghệ Wing Chun Magnetic switch, mang đến cảm giác gõ tuyệt vời khi chơi game. Đặc tính này không chỉ tăng cường độ nhạy mà còn giúp giảm thiểu độ trễ, cho phép người dùng phản ứng nhanh hơn trong những tình huống gay cấn.\n\nPhản hồi nhanh chóng\nVới khả năng kích hoạt nhanh chóng, bàn phím AULA WIN68HE MAX cho phép các game thủ thực hiện các thao tác một cách mượt mà và chính xác. Điều này cực kỳ quan trọng trong các trò chơi đòi hỏi sự nhanh nhạy và chính xác cao.\n\nTính năng chống nước\nMột ưu điểm khác của bàn phím AULA WIN68HE MAX là tính năng chống nước, giúp bảo vệ sản phẩm khỏi những sự cố không mong muốn. Điều này không chỉ gia tăng độ bền mà còn mang lại sự yên tâm cho người dùng trong quá trình sử dụng.\n\nPhím có thể thay thế dễ dàng\nBàn phím AULA WIN68HE MAX còn cho phép người dùng thay thế các phím một cách dễ dàng. Điều này vô cùng tiện lợi cho những ai muốn tùy chỉnh sản phẩm theo sở thích cá nhân hoặc thay thế phím sau một thời gian sử dụng.\n\nKết nối có dây ổn định\nVới khả năng kết nối có dây, bàn phím AULA WIN68HE MAX đảm bảo tín hiệu ổn định và không bị gián đoạn trong quá trình chơi game. Người dùng có thể hoàn toàn yên tâm về độ trễ hoặc mất kết nối khi phản xạ nhanh trong những play game căng thẳng.\n\nKhả năng tương thích rộng rãi\nAULA WIN68HE MAX tương thích với nhiều hệ điều hành, từ Windows đến Mac, giúp người dùng dễ dàng sử dụng mà không lo ngại về vấn đề tương thích.',
        1, 'https://product.hstatic.net/200000722513/product/imgi_146_img_6524_4ff18f3fe38b4662aba41feea5c2d9de.png'
    );
