INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Razer BlackWidow V4 75% Phantom Green Edition', 'ban-phim-razer-blackwidow-v4-75-phantom-green-edition', 15,
        '{"Thương hiệu": "Razer", "Bảo hành": "24 tháng"}',
        'Bàn phím Razer BlackWidow V4 75% Phantom Green Edition là một trong những sản phẩm nổi bật trong dòng bàn phím chơi game của Razer. Với thiết kế gọn gàng, tính năng vượt trội và cảm giác gõ tuyệt vời, sản phẩm này đã thu hút sự chú ý của nhiều game thủ. Trong bài viết này, chúng ta sẽ đánh giá chi tiết sản phẩm này về các đặc tính và tính năng nổi bật.\n\n**Thiết kế 75% nhỏ gọn, tối ưu không gian**\n\nRazer BlackWidow V4 75% là lựa chọn lý tưởng cho những game thủ và người dùng muốn tối ưu hóa không gian làm việc mà vẫn giữ được đầy đủ chức năng cần thiết. Với thiết kế form factor 75%, bàn phím này lược bỏ cụm phím số, giúp trở nên nhỏ gọn hơn đáng kể so với bàn phím full-size, giải phóng không gian cho những pha lia chuột rộng rãi và linh hoạt hơn. Dù kích thước nhỏ hơn, bàn phím vẫn giữ lại đầy đủ các phím chức năng (F-row) và các phím điều hướng quan trọng, đảm bảo không ảnh hưởng đến trải nghiệm sử dụng hàng ngày hay trong game.\n\n**Chất liệu cao cấp**\n\nBề mặt bàn phím được làm từ vật liệu chất lượng cao và bền bỉ, giúp bảo vệ sản phẩm khỏi sự hao mòn trong quá trình sử dụng. Độ hoàn thiện tuyệt vời mang lại cảm giác cao cấp cho người dùng.\n\n**Switch Razer Phantom Green**\n\nMột trong những điểm nổi bật của Bàn phím Razer BlackWidow V4 75% là việc sử dụng Switch Razer Phantom Green. Đây là loại switch cơ học được thiết kế đặc biệt để mang lại trải nghiệm gõ tốt nhất với âm thanh êm ái và độ nhạy cao. Người dùng có thể cảm nhận được từng cú gõ rõ ràng, từ đó nâng cao hiệu suất trong game.\n\n**LED RGB tùy chỉnh**\n\nPhiên bản Phantom Green Edition của BlackWidow V4 75% không chỉ mang đến vẻ ngoài độc đáo mà còn được trang bị hệ thống chiếu sáng Razer Chroma™ RGB nâng cao. Với đèn LED cho mỗi phím và ánh sáng underglow từ hai bên và mặt trước, bạn có thể tạo ra vô số hiệu ứng ánh sáng rực rỡ và đồng bộ hóa với các thiết bị Razer Chroma khác thông qua phần mềm Razer Synapse. Điều này biến bàn phím của bạn thành một phần của hệ sinh thái ánh sáng sống động, thể hiện phong cách cá nhân và nâng tầm không gian gaming.\n\n**Chức năng macro linh hoạt**\n\nTính năng ghi macro trên Razer BlackWidow V4 75% là một điểm cộng lớn. Người dùng có thể dễ dàng ghi lại các phím tắt và lệnh phức tạp để sử dụng trong game, giúp tối ưu hóa trải nghiệm chơi game của mình.\n\n**Khả năng chống bóng mờ**\n\nBàn phím Razer BlackWidow V4 75% được trang bị chức năng chống bóng mờ, cho phép nhận diện nhiều phím cùng lúc mà không gặp phải hiện tượng mất tín hiệu. Điều này đặc biệt hữu ích trong các tựa game yêu cầu độ chính xác cao.\n\n**Trải nghiệm gõ cao cấp với hot-swappable và foam damping**\n\nBlackWidow V4 75% được thiết kế để mang lại trải nghiệm gõ phím đỉnh cao. Tính năng hot-swappable cho phép người dùng dễ dàng thay đổi switch mà không cần hàn, mở ra cánh cửa vô tận cho việc tùy chỉnh cảm giác gõ theo sở thích cá nhân. Đi kèm với đó là cấu trúc gasket-mounted và nhiều lớp foam damping, bao gồm foam PCB và foam case. Sự kết hợp này giúp giảm thiểu đáng kể tiếng ồn và rung động khi gõ, tạo ra âm thanh gõ phím êm ái, đầm chắc và cực kỳ dễ chịu, biến mỗi lần nhấn phím thành một trải nghiệm tinh tế.\n\nBàn phím Razer BlackWidow V4 75% Phantom Green Edition không chỉ mang lại trải nghiệm gõ ấn tượng với thiết kế nhỏ gọn, mà còn đầy đủ tính năng cần thiết cho những game thủ chuyên nghiệp. Với chất lượng hoàn thiện cao, switch cơ học tối ưu cùng chức năng tùy chỉnh linh hoạt, sản phẩm này xứng đáng là lựa chọn hàng đầu cho những ai đang tìm kiếm một bàn phím gaming hoàn hảo. Hãy trải nghiệm ngay để cảm nhận sự khác biệt mà sản phẩm này mang lại!',
        1, 'https://product.hstatic.net/200000722513/product/ackwidow-v4-75-phantom-green-edition-rgb-hotswap-rz03-05003300-r3m1-08_f45889ee49404b11b7e7b0440aa3a96a.jpg'
    );
