INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron B1P-K2 White Scissor', 'ban-phim-keychron-b1p-k2-white-scissor', 10,
        '{"Loại bàn phím": "Bàn phím Low-Profile không dây", "Layout bàn phím": "75%", "Số phím bấm": "79 phím", "Chuẩn kết nối": "Có thể tháo rời Wired USB-C / Bluetooth / Wireless 2.4GHz", "Chất liệu bàn phím": "Khung phím: Nhựa ABS", "Switch bàn phím": "Scissor Mechanism", "Keycaps bàn phím": "ABS, Low-Profile", "Loại pin": "Pin sạc", "Dung lượng pin": "800 mAh", "Thời lượng pin": "Lên đến 8 tháng", "Phần mềm": "Keychron Launcher", "Kích thước": "296 x 130 mm", "Trọng lượng": "425g"}',
        'Bàn phím Keychron B1P-K2 White Scissor là sự kết hợp hoàn hảo giữa thiết kế tối giản, sự thoải mái khi gõ phím và tính di động cao, lý tưởng cho cả công việc văn phòng lẫn giải trí. Với gam màu trắng tinh khôi, bàn phím mang đến một vẻ ngoài sang trọng và hiện đại, dễ dàng hòa hợp với mọi không gian làm việc.\n\nĐiểm nhấn chính của sản phẩm là việc sử dụng switch scissor, mang lại cảm giác gõ phím êm ái, hành trình phím ngắn và độ nhạy cao. Điều này giúp giảm thiểu tiếng ồn và mang lại trải nghiệm gõ mượt mà, ít mỏi tay ngay cả khi sử dụng trong thời gian dài.\n\nThiết kế layout 75% độc đáo giúp tối ưu hóa không gian trên bàn làm việc mà vẫn giữ đầy đủ các phím chức năng quan trọng, bao gồm cả cụm phím mũi tên và các phím điều hướng. Điều này làm cho bàn phím trở nên linh hoạt, phù hợp với nhiều nhu cầu sử dụng khác nhau.\n\nKhả năng kết nối đa dạng là một ưu điểm vượt trội khác của Keychron B1P-K2. Bàn phím hỗ trợ cả kết nối có dây qua cổng USB-C tiện lợi và kết nối không dây Bluetooth 5.1, cho phép bạn dễ dàng chuyển đổi giữa tối đa 3 thiết bị, từ máy tính để bàn, laptop đến máy tính bảng hoặc điện thoại thông minh. Điều này mang lại sự linh hoạt tối đa cho người dùng thường xuyên di chuyển hoặc làm việc trên nhiều thiết bị.\n\nChất liệu vỏ phím cao cấp, bền bỉ cùng keycaps PBT chất lượng cao (tùy chọn) đảm bảo độ bền màu và chống mài mòn hiệu quả, giữ cho bàn phím luôn như mới. Bàn phím còn được trang bị đèn LED trắng đơn sắc, hỗ trợ nhiều chế độ hiển thị khác nhau, giúp bạn làm việc hiệu quả hơn trong điều kiện ánh sáng yếu và tăng thêm phần thẩm mỹ cho không gian.\n\nVới kích thước nhỏ gọn và tính năng đa dạng, Keychron B1P-K2 White Scissor là một lựa chọn tuyệt vời cho những ai tìm kiếm một bàn phím chất lượng cao, mang lại sự thoải mái, hiệu quả và phong cách.',
        1, 'https://product.hstatic.net/200000722513/product/download__8__0b78c2fdff4b4fe9afd9af76f5c014d3.png'
    );
