INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron X5-A3 Black Brown Switch', 'ban-phim-keychron-x5-a3-black-brown-switch', 10,
        '{"Thương hiệu": "Keychron", "Tên sản phẩm": "Keychron X5", "Layout": "96%", "Số lượng phím": "98 phím", "Chất liệu vỏ": "Nhựa", "Màu sắc": "Đen", "Keycap": "ABS Doubleshot - OEM Profile", "Switch": "Keychron Brown Switch", "LED": "LED Đỏ", "Kết nối": "Có dây", "Loại dây": "USB-C sang USB-A", "Kích thước (mm)": "380.5 x 133.7 x 38.6 mm"}',
        'Bàn phím Keychron X5-A3 Black Brown Switch là một lựa chọn tuyệt vời cho những người yêu thích trải nghiệm gõ phím cơ chất lượng cao với thiết kế hiện đại và tính năng linh hoạt. Sở hữu layout TKL (Tenkeyless) 87 phím, X5-A3 tối ưu hóa không gian bàn làm việc của bạn mà vẫn giữ nguyên các phím chức năng cần thiết. Phiên bản Black với gam màu đen huyền bí mang lại vẻ ngoài sang trọng, tinh tế và dễ dàng phối hợp với mọi setup.\n\nĐiểm nhấn nổi bật của bàn phím này là việc sử dụng Cherry MX Brown Switch, một loại switch cơ học được ưa chuộng bởi sự cân bằng giữa cảm giác gõ Tactile nhẹ nhàng và âm thanh clicky vừa phải. Điều này tạo nên một trải nghiệm gõ phím êm ái, nhạy bén, phù hợp cho cả công việc văn phòng lẫn chơi game, mà không gây khó chịu cho người xung quanh.\n\nKeychron X5-A3 còn được trang bị kết nối Bluetooth 5.1 tiên tiến, cho phép ghép nối với tối đa 3 thiết bị cùng lúc, chuyển đổi nhanh chóng giữa laptop, tablet hay điện thoại. Bên cạnh đó, bàn phím còn hỗ trợ kết nối có dây qua cổng USB-C, đảm bảo sự ổn định và tốc độ truyền dữ liệu tối ưu.\n\nChất liệu keycap PBT cao cấp với profile OEM mang lại cảm giác bấm chắc chắn, bền bỉ, chống bám vân tay và mài mòn hiệu quả. Đèn nền LED trắng đơn sắc giúp bạn dễ dàng thao tác trong điều kiện ánh sáng yếu và tạo điểm nhấn thẩm mỹ cho không gian làm việc. Khả năng tương thích đa nền tảng (Windows, macOS, Linux) cùng với việc hỗ trợ các phím tắt đa phương tiện giúp tăng cường hiệu suất làm việc và giải trí.\n\nKeychron X5-A3 Black Brown Switch không chỉ là một công cụ gõ phím mà còn là một phụ kiện thể hiện phong cách và đẳng cấp của người dùng.',
        1, 'https://product.hstatic.net/200000722513/product/download__6__7d2644b4c3474e8da37d752ab2662fef.png'
    );
