INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron K8M-J3 Black Brown Switch', 'ban-phim-keychron-k8m-j3-black-brown-switch', 10,
        '{"Nhà sản xuất": "Keychron", "Loại bàn phím": "Bàn phím cơ không dây", "Layout bàn phím": "80% TKL (ANSI)", "Số phím bấm": "87 phím", "Chuẩn kết nối": "Wireless 2.4GHz / Bluetooth 5.1 / Có dây USB-C", "Chất liệu bàn phím": "Khung phím: Nhôm CNC", "Switch bàn phím": "Keychron Super Red / Super Brown / Super Banana", "Keycaps bàn phím": "PBT Doubleshot, Profile OSA", "Đèn nền": "LED RGB", "N-Key Rollover": "Có", "Polling Rate": "2.4GHz: Lên đến 1000Hz; Bluetooth 5.1: Lên đến 90Hz", "Bộ nhớ trong": "Có", "Loại pin": "Pin sạc", "Dung lượng pin": "4000 mAh", "Thời lượng pin": "Có đèn nền: Lên đến 30 giờ; Không đèn nền: Lên đến 180 giờ", "Phần mềm": "Keychron Launcher", "Kích thước": "354.21 x 124.18 mm", "Trọng lượng": "1158g"}',
        'Khám phá sự kết hợp hoàn hảo giữa phong cách, hiệu năng và tính linh hoạt với Bàn phím cơ Keychron K8M-J3 Black Brown Switch. Được thiết kế dành cho những người dùng đòi hỏi cao, K8M-J3 mang đến trải nghiệm gõ phím vượt trội, lý tưởng cho công việc, giải trí và lập trình. Với layout Tenkeyless (TKL) gọn gàng, chiếc bàn phím này tối ưu không gian bàn làm việc mà vẫn giữ lại đầy đủ các phím chức năng cần thiết.\n\nĐiểm nhấn của phiên bản K8M-J3 chính là bộ switch Keychron/Gateron Brown. Loại switch tactile này nổi tiếng với phản hồi xúc giác êm ái, rõ ràng ngay tại điểm kích hoạt, mang lại cảm giác gõ phím \"đã tay\" mà không gây ra tiếng ồn quá lớn – sự cân bằng lý tưởng cho mọi môi trường. Đặc biệt, với tính năng hot-swappable, bạn có thể dễ dàng thay đổi các loại switch khác mà không cần hàn, cho phép tùy chỉnh trải nghiệm gõ phím theo sở thích cá nhân.\n\nBàn phím hỗ trợ kết nối kép linh hoạt: không dây qua Bluetooth 5.1 ổn định (kết nối tối đa 3 thiết bị cùng lúc, chuyển đổi mượt mà giữa các hệ điều hành Mac/Windows/Linux) và có dây qua cổng USB Type-C tiện lợi. Vẻ ngoài màu đen sang trọng cùng với viền nhôm chắc chắn không chỉ tạo nên vẻ ngoài chuyên nghiệp mà còn tăng cường độ bền bỉ. Hệ thống đèn nền RGB rực rỡ với nhiều chế độ chiếu sáng giúp nâng tầm thẩm mỹ và khả năng làm việc trong điều kiện thiếu sáng.\n\nThời lượng pin ấn tượng, keycaps chất lượng cao và khả năng tương thích vượt trội với đa hệ điều hành biến Keychron K8M-J3 thành lựa chọn lý tưởng cho bất kỳ ai đang tìm kiếm một chiếc bàn phím cơ đa năng, hiệu suất cao và cực kỳ đáng tin cậy để nâng tầm trải nghiệm gõ phím của mình.',
        1, 'https://product.hstatic.net/200000722513/product/download__5__ee4ac50aa2c94abda6039338a5e2b8f6.png'
    );
