INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Keychron K5M-B3 Black Brown Switch', 'ban-phim-keychron-k5m-b3-black-brown-switch', 10,
        '{"Hãng sản xuất": "Keychron", "Model": "K5M-B1 (Gateron Red Switch) / K5M-B3 (Gateron Brown Switch)", "Kết nối": "2.4GHz / Bluetooth 5.1 / Cáp USB Type-C", "Chất liệu keycap": "PBT Double-Shot", "Số phím": "108 phím", "Profile": "Low profile", "Đèn nền": "RGB", "Switch": "Gateron Red / Brown", "Độ bền Switch": "50 triệu lần nhấn", "Keyboard Rollover": "N-Key", "Kích thước": "424 x 119 mm", "Trọng lượng": "720 g", "Khả năng tương thích": "macOS / Windows / Linux", "Tính năng": "Foam tiêu âm IXPE / Hỗ trợ QMK & VIA", "Layout": "Fullsize", "Pin": "2000mAh", "Bảo hành": "12 tháng"}',
        'Bàn phím Keychron K5M-B3 Black Brown Switch là một trong những mẫu bàn phím cơ đang được ưa chuộng trong cộng đồng game thủ và người dùng văn phòng. Với thiết kế hiện đại, tính năng đa dạng và chất lượng hoàn thiện cao, sản phẩm này hứa hẹn sẽ mang đến cho người dùng những trải nghiệm tuyệt vời nhất. Trong bài viết này, chúng ta sẽ cùng nhau đánh giá chi tiết các đặc điểm và tính năng nổi bật của bàn phím Keychron này.\n\nThiết kế hiện đại và tinh tế\nBàn phím Keychron K5M-B3 có kiểu dáng mỏng nhẹ với lớp vỏ nhôm anodized bền bỉ, tạo cảm giác chắc chắn và sang trọng. Mặt phím được thiết kế với keycap PBT có độ bền cao, giúp bàn phím luôn giữ được độ mới sau một thời gian dài sử dụng.\n\nBàn phím Keychron K5M-B3 Black Brown Switch\n\nTính năng kết nối không dây và có dây đa dạng\nVới khả năng kết nối đa dạng, bạn có thể dễ dàng chuyển đổi giữa chế độ không dây qua Bluetooth 5.1 và kết nối có dây qua USB-C. Bàn phím có thể kết nối với nhiều thiết bị cùng lúc, bao gồm cả máy tính để bàn, laptop và smartphone, mang lại sự thuận tiện và linh hoạt cho người dùng.\n\nBàn phím Keychron K5M-B3 Black Brown Switch\n\nSwitch Brown êm ái và nhạy bén\nBàn phím Keychron K5M-B3 được trang bị switch brown, mang đến cảm giác gõ êm ái và không quá ồn ào. Điều này rất lý tưởng cho những ai làm việc trong không gian chung hoặc yêu thích sự yên tĩnh. Các keycap có độ nảy tốt, giúp thao tác gõ phím trở nên chính xác và dễ dàng hơn.\n\nBàn phím Keychron K5M-B3 Black Brown Switch\n\nHệ thống đèn nền RGB tùy chỉnh\nMột trong những điểm nổi bật của bàn phím này là hệ thống đèn nền RGB có thể tùy chỉnh. Người dùng có thể dễ dàng lựa chọn nhiều hiệu ứng đèn khác nhau để tạo điểm nhấn cho không gian làm việc hoặc chơi game của mình. Ngoài ra, hệ thống ánh sáng còn giúp bạn gõ phím dễ dàng hơn trong điều kiện ánh sáng yếu.\n\nBàn phím Keychron K5M-B3 Black Brown Switch\n\nQMK và VIA hỗ trợ tùy chỉnh\nKeychron K5M-B3 hỗ trợ QMK và VIA, cho phép người dùng thực hiện tùy chỉnh sâu hơn cho bàn phím của mình. Bạn có thể dễ dàng thay đổi chức năng của các phím, tạo macro và điều chỉnh ánh sáng RGB theo ý thích riêng. Điều này giúp người dùng có thể tối ưu hóa bàn phím theo nhu cầu sử dụng cá nhân.\n\nPin dung lượng lớn và thời gian sử dụng dài\nBàn phím Keychron K5M-B3 được trang bị pin dung lượng lớn, cho phép sử dụng liên tục trong nhiều giờ mà không cần phải sạc lại thường xuyên. Điều này rất phù hợp cho những người phải làm việc lâu dài hoặc thường xuyên di chuyển.',
        1, 'https://product.hstatic.net/200000722513/product/download__4__0c03d83821ce4011b0ee742e1cc87eaa.png'
    );
