INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Leobog Hi86 TM (Trắng đen/ Nimbus V3 switch) HI8602', 'ban-phim-leobog-hi86-tm-trang-den-nimbus-v3-switch', 21,
        '{"Tên sản phẩm": "Bàn phím Leobog Hi86 TM", "Mã sản phẩm": "HI8602", "Thương hiệu": "Leobog", "Layout": "86 phím", "Màu sắc": "Trắng đen", "Loại Switch": "Leobog Nimbus V3 Linear", "Loại Keycap": "PBT Doubleshot", "Profile Keycap": "KSA Profile", "Kiểu kết nối": "Ba chế độ (USB-C, Bluetooth, 2.4GHz)", "Hotswap": "Có, 5-pin", "Đèn nền": "RGB", "Chất liệu vỏ": "Nhựa ABS", "Kiểu lắp ráp": "Gasket Mount", "Tấm tiêu âm": "Có (PORON Foam)", "Dung lượng pin": "3000 mAh", "Tần số phản hồi": "1000 Hz (có dây/2.4GHz)", "Tương thích hệ điều hành": "Windows, macOS, Linux", "Kích thước": "Khoảng 365 x 135 x 45 mm", "Trọng lượng": "Khoảng 1000 g"}',
        'Bàn phím Leobog Hi86 TM được thiết kế với kích thước tiêu chuẩn nhỏ hơn cả bàn phím TKL, phù hợp với hầu hết các không gian làm việc và gaming. Với kiểu dáng nhỏ gọn nhưng vẫn đầy đủ các phím chức năng, người dùng không gặp khó khăn khi sử dụng hay mang theo.\n\nĐèn nền RGB\nMột trong những điểm nhấn của bàn phím máy tính này chính là hệ thống đèn nền RGB sống động. Người dùng có thể tùy chỉnh nhiều chế độ chiếu sáng khác nhau, tạo nên không gian gaming ấn tượng và phong cách cá nhân.\n\nSwitch Nimbus V3\nNimbus V3 switch là một loại công tắc độc quyền mà Leobog áp dụng cho sản phẩm của mình. Loại switch này mang lại cảm giác gõ nảy, âm thanh rõ nét và độ bền cao, khiến nó trở thành lựa chọn lý tưởng cho cả game thủ lẫn những người làm việc văn phòng.\n\nĐộ phản hồi nhanh chóng\nVới khả năng phản hồi cực nhanh, bàn phím Leobog Hi86 TM giúp người dùng thực hiện các thao tác một cách nhanh chóng và chính xác. Điều này rất quan trọng trong các trận đấu game, nơi mà mỗi giây trôi qua đều có thể quyết định thắng bại.\n\nChế độ kết nối đa dạng\nSản phẩm hỗ trợ kết nối Bluetooth, USB và 2.4GHz, mang đến sự linh hoạt cho người dùng trong việc lựa chọn phương thức kết nối phù hợp. Điều này giúp bàn phím có thể sử dụng với nhiều thiết bị khác nhau, từ laptop, PC đến máy tính bảng.\n\nChất liệu và độ bền\nBàn phím Leobog Hi86 TM được làm từ các chất liệu cao cấp, đảm bảo độ bền bỉ và sức chịu đựng dưới áp lực sử dụng cao. Các phím bấm được hoàn thiện tỉ mỉ, không chỉ đem lại trải nghiệm gõ tốt mà còn giữ được tính thẩm mỹ theo thời gian.',
        1, 'https://product.hstatic.net/200000722513/product/1_a7d3d7cf71f84a21becb1331e6053aab.png'
    );
