INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím ROG Strix Scope RX TKL Wireless Deluxe (90MP02J0-BKUA00)', 'ban-phim-rog-strix-scope-rx-tkl-wireless-deluxe-90mp02j0-bkua00', 3,
        '{"Nhà Sản Xuất": "Asus", "Tình Trạng": "Mới 100% - Fullbox"}',
        'Đắm chìm vào thế giới game đỉnh cao với Bàn phím ROG Strix Scope RX TKL Wireless Deluxe – vũ khí tối thượng dành cho mọi game thủ chuyên nghiệp. Được thiết kế để mang lại hiệu suất vượt trội, sự tiện lợi tối đa và phong cách không thể nhầm lẫn, chiếc bàn phím Tenkeyless (TKL) này là sự kết hợp hoàn hảo giữa công nghệ tiên tiến và thiết kế tinh gọn.\n\nTrái tim của ROG Strix Scope RX TKL Wireless Deluxe chính là các Switch quang cơ ROG RX độc quyền. Với điểm kích hoạt gần như tức thì, độ bền vượt trội lên đến 100 triệu lần nhấn và hành trình phím mượt mà, nhất quán nhờ cơ chế ổn định chéo, mỗi cú nhấn đều được ghi nhận chính xác và nhanh chóng, mang lại lợi thế quyết định trong mọi trận chiến.\n\nThiết kế TKL nhỏ gọn giải phóng không gian bàn làm việc của bạn, cho phép di chuột rộng hơn và mang lại tư thế chơi game tự nhiên, thoải mái hơn. Đồng thời, khả năng kết nối không dây ba chế độ mang đến sự linh hoạt tuyệt đối: ROG SpeedNova 2.4 GHz cho độ trễ siêu thấp, Bluetooth để dễ dàng ghép nối với nhiều thiết bị, và USB-C có dây để sạc nhanh và chơi game không ngừng nghỉ với hiệu suất tối đa. Với thời lượng pin ấn tượng, bạn có thể chiến game hàng giờ liền mà không lo gián đoạn.\n\nVỏ ngoài được chế tác từ hợp kim nhôm nguyên khối mang lại vẻ ngoài cao cấp, bền bỉ và cứng cáp, chịu được cường độ sử dụng cao. Các tính năng tiện ích như phím Stealth ẩn mọi ứng dụng và tắt âm thanh ngay lập tức, cùng phím chuyển đổi nhanh giữa chức năng và media, tối ưu hóa trải nghiệm sử dụng.\n\nHệ thống đèn RGB Aura Sync sống động, có thể tùy chỉnh không giới hạn, cho phép bạn thể hiện cá tính riêng và đồng bộ hóa với hệ sinh thái ROG của mình. ROG Strix Scope RX TKL Wireless Deluxe không chỉ là một chiếc bàn phím, mà là một tuyên ngôn về hiệu năng và phong cách. Hãy sẵn sàng thống trị mọi đấu trường ảo với công cụ hoàn hảo này trong tay!',
        1, 'https://product.hstatic.net/200000722513/product/h525_299ae775c80b4882b6386797f3aa9b59.png'
    );
