INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Corsair K70 PRO TKL White', 'ban-phim-corsair-k70-pro-tkl-white', 5,
        '{"Layout": "Tenkeyless (TKL)", "Switch": "Cherry MX Red / Blue / Brown", "Keycap": "PBT Double-shot", "Tốc độ phản hồi": "8000Hz polling rate", "Kết nối": "USB-C có thể tháo rời", "Đèn nền": "RGB tùy chỉnh cho từng phím", "Bộ nhớ onboard": "Lưu nhiều profile", "Chất liệu khung": "Nhôm cao cấp"}',
        'Corsair K70 PRO TKL White là một kiệt tác công nghệ, kết hợp hiệu suất đỉnh cao, thiết kế tinh tế và trải nghiệm gõ phím tuyệt vời. Với phiên bản màu trắng độc đáo, chiếc bàn phím này không chỉ là một công cụ chơi game hay làm việc mạnh mẽ mà còn là điểm nhấn thẩm mỹ cho không gian của bạn. Sử dụng switch cơ học Cherry MX RGB Speed Silver siêu nhanh, K70 PRO TKL White mang lại phản hồi tức thời và độ bền bỉ ấn tượng, đáp ứng mọi nhu cầu từ những trận game esports căng thẳng đến những tác vụ gõ văn bản đòi hỏi độ chính xác cao. Công nghệ AXON Hyper-Processing độc quyền của Corsair đảm bảo tần số quét lên đến 8000Hz, mang đến tốc độ truyền tín hiệu nhanh hơn tới 8 lần so với các bàn phím gaming tiêu chuẩn, giúp bạn luôn dẫn trước đối thủ một bước. Hệ thống đèn LED RGB per-key sống động và rực rỡ, với khả năng tùy chỉnh đa dạng thông qua phần mềm iCUE, cho phép bạn tạo ra những hiệu ứng ánh sáng độc đáo, đồng bộ với các thiết bị Corsair khác hoặc phản ứng theo hành động trong game. Thiết kế TKL (Tenkeyless) nhỏ gọn loại bỏ phần bàn phím số, tối ưu hóa không gian di chuột và mang lại cảm giác thoải mái khi sử dụng. Khung bàn phím bằng nhôm chải xước không chỉ tăng cường độ chắc chắn mà còn tạo nên vẻ ngoài sang trọng. Keycap PBT double-shot với ký tự chống mài mòn đảm bảo độ bền màu và cảm giác bấm cao cấp. Ngoài ra, K70 PRO TKL White còn được trang bị các phím media chuyên dụng, bánh xe âm lượng đa chức năng, bộ nhớ trong lưu trữ profile và cổng kết nối USB Passthrough tiện lợi. Đây là sự lựa chọn hoàn hảo cho những ai tìm kiếm một chiếc bàn phím cơ cao cấp, hiệu năng vượt trội và phong cách ấn tượng.',
        1, 'https://product.hstatic.net/200000722513/product/k70_pro_tkl_white_render_01_82b433ed42094c2caf7a805d777ddc5a.png'
    );
