INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím cơ gaming DareU EK98 Pro Black Golden Cloud switch', 'ban-phim-co-gaming-dareu-ek98-pro-black-golden-cloud-switch', 25,
        '{"Tên sản phẩm": "Bàn phím cơ gaming DareU EK98 Pro", "Màu sắc": "Black Golden Cloud", "Loại Switch": "DareU ECSwitch (Golden Cloud)", "Layout": "98%", "Số phím": "98 phím", "Kết nối": "Có dây (USB-C)", "Đèn nền": "RGB", "Vật liệu keycap": "PBT", "Cấu trúc gasket": "Có", "Hỗ trợ": "Nhiều hệ điều hành", "Phần mềm": "Có (cho tùy chỉnh)", "Kích thước": "Khoảng 383 x 135 x 40 mm", "Trọng lượng": "Khoảng 1.1 kg", "Hot-swap": "Có", "Hỗ trợ NKRO": "Có", "Tần số polling": "1000Hz", "Loại dây cáp": "Dây bện có thể tháo rời"}',
        'Đánh giá chi tiết Bàn phím cơ gaming DareU EK98 Pro Black Golden Cloud switch\n\nBàn phím cơ gaming DareU EK98 Pro Black Golden Cloud switch là một sản phẩm nổi bật trong phân khúc bàn phím cơ, với thiết kế tinh tế, tính năng ưu việt và độ bền cao. Hôm nay, chúng ta sẽ cùng khám phá chi tiết về sản phẩm này để xem nó có xứng đáng để bạn đầu tư hay không.\n\n**Thiết kế và chất liệu**\n\nBàn phím Dareu EK98 Pro gây ấn tượng với thiết kế hiện đại, sự kết hợp màu đen bóng và chi tiết vàng sang trọng biến nó thành một phụ kiện trang trí đẹp mắt. Để đảm bảo tuổi thọ và sự ổn định trong quá trình sử dụng, Dareu đã lựa chọn những vật liệu chất lượng cao, trong đó có nhựa ABS chịu va đập tốt cho phần vỏ, mang lại độ bền và sự an tâm cho người dùng. Kích thước của Dareu EK98 Pro nhỏ gọn và cơ động, dễ dàng mang theo khi di chuyển. Đặc biệt, việc bố trí các phím cũng chứng minh được tính hợp lý trong việc sử dụng.\n\n**Tính năng nổi bật**\n\nDareu EK98 Pro được trang bị switch Golden Cloud, một loại switch đặc biệt được chế tạo để tối ưu hóa cảm giác gõ phím. Độ mượt mà và độ nảy tốt của switch này đáp ứng hoàn hảo nhu cầu của cả game thủ và người dùng văn phòng. Về mặt thẩm mỹ, bàn phím sở hữu hệ thống đèn nền RGB sống động với nhiều chế độ chiếu sáng khác nhau, cho phép bạn tạo ra một không gian làm việc độc đáo và đầy màu sắc.\n\n**Tính năng chống ghosting**\n\nDareu EK98 Pro sở hữu khả năng chống ghosting toàn diện, đảm bảo mọi lệnh nhấn phím đều được ghi nhận một cách chính xác, ngay cả khi bạn thao tác đồng thời nhiều phím. Điều này đặc biệt quan trọng trong các trò chơi đòi hỏi tốc độ phản xạ cao. Ngoài ra, phần mềm chuyên dụng đi kèm cho phép người dùng cá nhân hóa bàn phím bằng cách tùy chỉnh chức năng của các phím và thiết lập các macro chuyên biệt, nâng cao hiệu suất làm việc và giải trí.\n\n**Kết nối ổn định**\n\nĐể đảm bảo trải nghiệm gaming mượt mà và không gián đoạn, bàn phím cơ gaming DareU EK98 Pro Black Golden Cloud switch được trang bị kết nối USB có dây vô cùng linh hoạt. Kết nối có dây mang đến sự ổn định và độ tin cậy cao. Đặc biệt, đối với game thủ, độ trễ là một yếu tố tối quan trọng, và kết nối USB có dây của Dareu EK98 Pro đảm bảo thời gian phản hồi gần như tức thì giữa thao tác của bạn và hành động trên màn hình, mang lại lợi thế cạnh tranh trong những trận game căng thẳng.\n\nBàn phím cơ gaming DareU EK98 Pro Black Golden Cloud switch là một sản phẩm đáng giá cho những ai đang tìm kiếm một bàn phím với chất lượng tốt và tính năng ưu việt. Với thiết kế sang trọng, switch Golden Cloud êm ái, cùng nhiều tính năng hữu ích, đây chắc chắn sẽ là lựa chọn phù hợp cho cả game thủ và những người làm việc văn phòng. Hy vọng bài đánh giá này sẽ giúp bạn có cái nhìn tổng quan về sản phẩm. Nếu bạn đang tìm kiếm một bàn phím cơ chất lượng, đừng bỏ qua Dareu EK98 Pro!',
        1, 'https://product.hstatic.net/200000722513/product/ek98_pro_black-golden__2__2a64b4_d5dbe79335304f538cc418aee522102a.png'
    );
