INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím cơ gaming E-Dra EK375v2 Beta Linear switch', 'ban-phim-co-gaming-e-dra-ek375v2-beta-linear-switch', 23,
        '{"Thương hiệu": "E-Dra", "Bảo hành": "24 tháng"}',
        'Đánh giá chi tiết bàn phím cơ gaming E-Dra EK375v2 Beta Linear switch\n\nE-Dra, cái tên quen thuộc với cộng đồng game thủ ở phân khúc học sinh và sinh viên nhờ mức giá hợp lý và chất lượng ổn định từ những sản phẩm mà thương hiệu mang lại. Không để chúng ta chờ đợi, E-Dra EK375v2 Beta Linear switch chính thức xuất hiện trên thị trường và hy vọng sẽ trở thành một trong những lựa chọn hàng đầu dành cho game thủ.\n\nThiết kế tối giản, mạnh mẽ với layout 75%\n\nE-Dra EK375v2 Beta sở hữu ngôn ngữ thiết kế tối giản với tone màu đen chủ đạo, toát lên vẻ huyền bí, cùng màu xanh trên keycap tạo nên điểm nhấn cho chiếc bàn phím cơ này. Với layout 75% đang thịnh hành và núm xoay trendy, chiếc bàn phím E-Dra đáp ứng tốt nhu cầu làm việc cơ bản và giải trí trên PC Gaming lẫn laptop, tối ưu tuyệt vời cho không gian sử dụng.\n\nBàn phím cơ E-Dra EK375v2 Beta còn đi kèm với hệ thống đèn LED Rainbow rực rỡ, càng tô điểm cho chiếc bàn phím gaming giá rẻ này trong mọi không gian và dễ dàng trở thành một vật trang trí cho góc chơi game.\n\nTrải nghiệm gõ phím mượt mà\n\nỞ phiên bản Beta này, E-Dra EK375v2 trang bị switch Linear do chính E-Dra phát triển. Chiếc switch cho cảm giác gõ mượt mà, kết hợp cùng phần foam được lót trong case sẽ hạn chế tối đa tiếng ồn, rất lý tưởng cho không gian làm việc yêu cầu sự tập trung và tránh gây ảnh hưởng đến người xung quanh.\n\nE-Dra trang bị cho EK375v2 Beta bộ keycap làm từ nhựa ABS với công nghệ in Doubleshot, giúp chữ không bị mờ sau thời gian dài sử dụng. Chất liệu phù hợp kết hợp với kiểu in hiện đại mang lại font chữ sắc nét cùng độ bền lý tưởng.',
