INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím DareU EK104 RGB Grey Black Dream Switch', 'ban-phim-dareu-ek104-rgb-grey-black-dream-switch', 25,
        '{"Tên sản phẩm":"DareU EK104 RGB Grey Black Dream Switch","Loại bàn phím":"Full-size","Số phím":104,"Keycap":"PBT Double-shot","Tương thích":"Windows 10/11","Kết nối":"Có dây USB","Độ dài dây":"1.8 mét","Đèn LED":"LED Rainbow, Led viền RGB","Tiện ích":"N-Key Rollover, Stabilizer cân chỉnh và lube sẵn, Có foam PCB Eva","Chất liệu":"Khung phím: Nhựa ABS, Plate: PC","Kích thước":"39.2 × 15 × 4 cm","Khối lượng":"845g","Hãng sản xuất":"DAREU"}',
        'Bàn phím cơ có dây Dareu EK104 RGB Grey-Black – Switch cơ học chất lượng cao\nBàn phím có thiết kế mạnh mẽ, hệ thống đèn RGB sống động và trải nghiệm gõ ấn tượng. Dareu EK104 là một lựa chọn phù hợp dành cho rất nhiều đối tượng khác nhau từ văn phòng đến game thủ.\n\nCảm giác gõ tuyệt vời đi kèm thiết kế mạnh mẽ\nDareu EK104 sở hữu thiết kế full-size với đầy đủ 104 phím, tông màu Grey - Black cá tính, phù hợp với mọi không gian. Phần khung bàn phím Dareu này chắc chắn, được hoàn thiện kỹ lưỡng, cho cảm giác cao cấp và bền bỉ theo thời gian.\n\nThiết kế bàn phím có dây Dareu EK104 RBG Grey – Black\nĐiểm mạnh nhất của EK104 chính là switch cơ học chính hãng Dareu với độ bền lên đến 50 triệu lần nhấn. Sản phẩm hỗ trợ cả switch DareU DREAM (Linear) và FIRE-FLY (Tactile) giúp khách hàng hoàn toàn có thể lựa chọn loại cơ học phù hợp với bản thân.\n\nTương thích nhiều nền tảng với kết nối có dây chuẩn USB\nBàn phím cơ có dây Dareu EK104 RBG Grey – Black được kết nối qua cổng USB có dây, đảm bảo tín hiệu ổn định, không độ trễ. Sản phẩm tương thích với các hệ điều hành hiện nay như Windows, Mac OS chỉ cần cắm dây vào là có thể dùng ngay.\n\nKhả năng tương thích bàn phím có dây Dareu EK104 RBG Grey – Black\nBên cạnh đó, điểm nhấn trên EK104 chính là hệ thống đèn nền RGB, cho phép người dùng thay đổi hàng chục hiệu ứng ánh sáng khác nhau. Sản phẩm có các chế độ như: chạy sóng, tĩnh, động,… rất đa dạng.',
        1, 'https://product.hstatic.net/200000722513/product/1_63d1f288ba0b4b70bf3a24101b14f0a3.png'
    );
