INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím cơ AKKO 5075B Plus TM Chicago Piano V3 Pro Switch', 'ban-phim-co-akko-5075b-plus-tm-chicago-piano-v3-pro-switch', 4,
        '{"Tên sản phẩm":"Bàn phím AKKO 5075B Plus Chicago V3 Pro Switch","Layout":"75%","LED nền":"RGB 16 triệu màu với nhiều chế độ, có LED 2 bên hông phím","Kết nối":"Dây / Bluetooth 5.0 / 2.4GHz","Pin":"3000mAh (Tiêu thụ 12mA/giờ ở chế độ không dây và không bật LED)","Kích thước":"335 × 146 × 42 mm","Trọng lượng":"Khoảng 1.1Kg","Cấu trúc":"Gasket mount + Plate PC","Keycap":"PBT Double-Shot, ASA profile, xuyên LED","Loại switch":"AKKO Switch v3 (Cream Yellow Pro)","Hỗ trợ":"NKRO, Multimedia, Macro, Khóa phím Win","Foam tiêu âm":"Có PCB foam, foam đáy và IXPE switch pad","Hotswap":"5 pin TTC socket","Phụ kiện":"1 sách hướng dẫn sử dụng, 1 dây USB Type-C to USB, 1 USB Receiver 2.4GHz, 5 switch tặng kèm, 1 che bụi bàn phím","Phần mềm":"AKKO Cloud Driver (Hỗ trợ Audio Visualizer ở chế độ 2.4GHz, keymap, chỉnh LED / Macro)","Tương thích":"Windows / MacOS / Linux (MacOS: Ctrl->Control, Windows->Command, Alt->Option, Mojave OS trở lên có thể điều chỉnh thứ tự phím)"}',
        'Bàn phím cơ AKKO 5075B Plus TM Chicago Piano V3 Pro Switch là một lựa chọn tuyệt vời cho những người đam mê bàn phím cơ, kết hợp hiệu năng mạnh mẽ với thiết kế độc đáo và chất lượng hoàn thiện cao cấp. Thuộc dòng 5075B Plus, bàn phím này mang đến sự cân bằng hoàn hảo giữa kích thước TKL (Tenkeyless) nhỏ gọn và các phím chức năng đầy đủ, phù hợp với mọi không gian làm việc và nhu cầu sử dụng.\n\nĐiểm nhấn nổi bật của phiên bản này chính là bộ keycap chủ đề Chicago Piano, lấy cảm hứng từ vẻ đẹp cổ điển và sang trọng, với tông màu đen chủ đạo kết hợp các chi tiết nhấn nhá tinh tế, tạo nên một diện mạo độc đáo và đầy phong cách. Chất liệu PBT cao cấp được sử dụng cho keycap, đảm bảo độ bền bỉ, chống mài mòn và không bị bóng dầu sau thời gian dài sử dụng.\n\nCông nghệ V3 Pro Switch mang đến trải nghiệm gõ phím mượt mà, phản hồi nhanh nhạy và độ trễ thấp, lý tưởng cho cả việc chơi game và gõ văn bản. Bàn phím hỗ trợ kết nối đa dạng với Bluetooth, Wireless 2.4GHz và cáp USB-C, cho phép bạn linh hoạt kết nối với nhiều thiết bị khác nhau.\n\nTính năng Hot-swap cho phép bạn dễ dàng thay thế switch mà không cần hàn, mở ra khả năng tùy chỉnh vô tận để phù hợp với sở thích cá nhân. Đèn nền RGB với nhiều hiệu ứng đa dạng, không chỉ tăng tính thẩm mỹ mà còn hỗ trợ làm việc trong điều kiện ánh sáng yếu.\n\nAKKO 5075B Plus TM Chicago Piano V3 Pro Switch không chỉ là một công cụ gõ phím, mà còn là một tác phẩm nghệ thuật trên bàn làm việc của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/multi-modes-4-1_71918eb58998485a8f37b214ada1caf0.png'
    );
