INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F98 PRO TM (Trắng/ Ice Crystal Switch) F98PB', 'ban-phim-aula-f98-pro-tm-trang-ice-crystal-switch', 22,
        '{"Tên sản phẩm":"Bàn phím cơ AULA F98 PRO TM","Độ bền phím":"60 triệu lần bấm","Kết nối":"3 chế độ có dây Type-C, không dây 2.4G và Bluetooth","Màu sắc":"2 màu đen trong suốt và trắng trong suốt","Keycap":"Nhựa PC trong suốt","Đèn nền":"LED RGB, 19 hiệu ứng ánh sáng","Loại switch":"Ice crystal switch","Hiệu ứng âm thanh":"Linear","Hỗ trợ Hot-Swap":"5 pin","Thiết kế":"Gasket mount","Núm xoay đa chức năng":"Có","Màn hình LED":"Hiển thị thông tin và hình ảnh","Mạch":"Ngược","Số lượng phím":95,"Điện áp/Dòng sạc":"DC 5V/≦600mA","Điện áp định mức":"DC 3.7V","Dung lượng pin":"Pin Li-ion có thể sạc lại 4000mAh","Thời gian sử dụng":"≧19 giờ (hiệu ứng ánh sáng mặc định); ≧42 giờ (tắt đèn nền và đèn bên)","Trọng lượng":"1060 g","Kích thước (Dài x Rộng x Cao)":"384,98 × 138,20 × 40,68 mm ±0,5","Hệ điều hành tương thích":"WIN XP, WIN7, WIN8, WIN10, Android, iOS, MAC","Phụ kiện kèm theo":"Sách hướng dẫn sử dụng, 4 Switch tặng kèm, Dây USB type-C, Dụng cụ thay keycap, Bộ chổi và khăn vệ sinh bàn phím"}',
        'Nâng tầm trải nghiệm gõ phím của bạn lên một đẳng cấp mới với Bàn phím AULA F98 PRO TM (Trắng/ Ice Crystal Switch) F98PB – một tuyệt tác kết hợp hoàn hảo giữa phong cách, hiệu suất và cảm giác gõ độc đáo. Với tông màu trắng tinh khôi, chiếc bàn phím này không chỉ là một công cụ mà còn là một điểm nhấn tinh tế cho không gian làm việc hay giải trí của bạn.\n\nĐiểm nhấn cốt lõi của AULA F98 PRO TM chính là hệ thống Ice Crystal Switch độc quyền. Mỗi phím bấm mang lại cảm giác mượt mà, trong trẻo như pha lê, cùng âm thanh gõ êm ái, thanh thoát nhưng vẫn cực kỳ rõ ràng và phản hồi. Đây là trải nghiệm âm thanh và xúc giác mà mọi tín đồ bàn phím cơ đều khao khát, lý tưởng cho cả những phiên làm việc dài cần sự yên tĩnh lẫn những trận đấu game căng thẳng cần độ chính xác cao.\n\nVới bố cục F98 thông minh, bàn phím tối ưu hóa không gian bàn làm việc trong khi vẫn giữ lại đầy đủ các phím chức năng và numpad tiện lợi. Chất lượng hoàn thiện cao cấp, từ khung sườn chắc chắn đến bộ keycaps bền bỉ, chống bóng dầu hiệu quả, đảm bảo độ bền vượt trội và giữ vững vẻ đẹp như mới theo thời gian. Khả năng kết nối Tri-Mode linh hoạt (Bluetooth, 2.4G không dây và USB-C có dây) cho phép bạn dễ dàng chuyển đổi giữa các thiết bị, mang lại sự tiện lợi tối đa.\n\nNgoài ra, AULA F98 PRO TM còn được trang bị đèn nền RGB rực rỡ với nhiều hiệu ứng ánh sáng đa dạng, cùng phần mềm tùy chỉnh mạnh mẽ giúp bạn cá nhân hóa mọi thứ từ ánh sáng đến gán macro, biến chiếc bàn phím thành người bạn đồng hành hoàn hảo, thể hiện cá tính riêng của bạn. Dù bạn là game thủ chuyên nghiệp, lập trình viên hay chỉ đơn giản là người yêu thích sự hoàn hảo, AULA F98 PRO TM (Trắng/ Ice Crystal Switch) F98PB chắc chắn sẽ là sự lựa chọn không thể bỏ qua.',
        1, 'https://product.hstatic.net/200000722513/product/f98_pro__2___1__111acbd8b1484094879b6e4699b1675e.png'
    );
