INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím cơ Mountain Everest MAX Xám MX Brown Switch', 'ban-phim-co-mountain-everest-max-xam-mx-brown-switch-1', 39,
        '{"Tên sản phẩm": "Bàn phím cơ Mountain Everest MAX", "Màu sắc": "Xám", "Loại Switch": "Cherry MX Brown", "Kiểu bàn phím": "Cơ học, Mô-đun", "Kết nối": "USB Type-C có dây", "Polling Rate": "1000 Hz", "Chất liệu khung": "Nhôm Anodized", "Chất liệu Keycap": "ABS", "Đèn nền": "RGB Per-Key", "Tính năng đặc trưng": "Media Dock tích hợp màn hình, Numpad từ tính tháo rời, USB Passthrough", "Phần mềm": "Mountain Base Camp", "Cáp": "USB Type-C bện, có thể tháo rời", "Tương thích HĐH": "Windows, macOS", "Kích thước (Bàn phím chính)": "366 x 136 x 40 mm", "Trọng lượng (Bàn phím chính)": "1000 g"}',
        'Mountain Everest Max - Cherry MX Brown Switch mở ra 1 khái niệm mới cho bàn phím cơ với nhiều tính năng hiện đại được tích hợp. Cấu tạo khung viền bằng nhôm phay xước nguyên khối chắc chắn cùng màu xám đẹp mắt, mang đến vẻ ngoài cứng cáp cùng độ bền tối ưu. \n\n \n\nMàn hình hiển thị có thể tùy chỉnh\nMàn hình hiển thị có thể tùy chỉnh\tChiếc bàn phím đầu tiên hỗ trợ màn hình hiển thị, cho phép dễ dàng điều khiển đa phương tiện, theo dõi hiệu suất hệ thống, thông tin hiển thị hoặc chuyển đổi cấu hình.\n \n\nPhím numpad có thể tháo rời\nNếu các công việc hàng ngày của bạn gắn liền với việc đánh số thường xuyên hoặc bạn có một tập lệnh phức tạp và thiết lập phím nóng, thì Everest Numpad là dành cho bạn. Nó được thiết kế để vừa khít và an toàn với Everest Max, có thể dễ dàng thiết lập cả ở bên trái và bên phải\tPhím numpad có thể tháo rời\n \n\n4 phím hiển thị có thể tùy chỉnh\n4 phím hiển thị có thể tùy chỉnh\tĐể tối đa hóa hiệu suất làm việc và giải trí của bạn, 4 phím hiển thị trên Everest Numpad có thể sửa đổi, mở ra nhiều tùy chọn cá nhân hóa nhằm mang đến sự tiện lợi cho riêng bạn\n \n\nDễ dàng thiết lập Streaming\nBase Camp có tính năng tích hợp chặt chẽ với OBS Studio để cho phép ghi âm, phát trực tuyến, chuyển tiếp và hơn thế nữa chỉ bằng một nút nhấn\tDễ dàng thiết lập Streaming\n \n\nThiết kế nâng chân đế độc đáo\nThiết kế nâng chân đế độc đáo\tThiết kế chân nâng độc đáo được cấp bằng sáng chế của Everest tạo ra một giá đỡ an toàn và chắc chắn, vượt xa các chân đế nhựa mỏng manh trên các mẫu bàn phím thế hệ trước. Ngoài ra, một bộ đệm lót cho phép bạn tìm độ cao hoàn hảo để điều chỉnh tư thế gõ phím sao cho thoải mái nhất\n \n\nÁnh sáng RGB rực rỡ\nTrang trí bàn làm việc của bạn với dải đèn LED RGB với 16,7 triệu màu và nhiều chế độ RGB có thể tùy chỉnh. Với ánh sáng RGB trên mỗi phím và dải LED xung quanh phần thân, hứa hẹn sẽ biến góc chơi game của bạn trở nên rực rỡ.\tÁnh sáng RGB rực rỡ\n \n\nPhím Stab cực êm \nPhím Stab cực êm \tĐộ ồn là 1 khía cạnh quan trọng trên 1 chiếc bàn phím cơ, Everest Max cũng không ngoại lệ. Trang bị phím stab cực êm được điều chỉnh tại nhà máy với Glaze ™ GPL 205 Grade 0.Loại bỏ tiếng lạch cạch khó chịu nhằm mang đến trải nghiệm gõ êm ái hơn\n \n\nHỗ trợ HotSwap dễ dàng\nEverest Max - Cherry MX Brown Switch cho phép người dùng tùy ý thay đổi switch với tính hotswap thông minh, nhằm mở ra nhiều tùy chọn cho những dân chơi bàn phím cơ',
        1, 'https://product.hstatic.net/200000722513/product/71105_ban_phim_gaming_mountain_everest_max_gray_1__1__ef75e9a656184ab19f263ab9eebfad4b.jpg'
    );
