INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F75 (Xám + Đen Gradient/ Reaper switch) F7505', 'ban-phim-aula-f75-xam-den-gradient-reaper-switch', 22,
        '{"Thương hiệu": "AULA", "Model": "F75", "Mã sản phẩm": "F7505", "Màu sắc": "Xám + Đen Gradient", "Loại switch": "Reaper switch", "Layout": "75%", "Kết nối": "Có dây USB-C", "Đèn nền": "RGB", "Hỗ trợ Hot-swap": "Có", "Vật liệu keycap": "PBT", "Chất liệu vỏ": "Nhôm CNC", "Pin": "Không có", "Tính năng đặc biệt": "Nút xoay điều chỉnh âm lượng"}',
        'Bàn phím AULA F75 là một trong những sản phẩm nổi bật trong dòng bàn phím gaming hiện nay. Với thiết kế độc đáo và tính năng tối ưu cho việc chơi game cũng như làm việc, AULA F75 đã thu hút sự chú ý của nhiều game thủ và người dùng công nghệ. Dưới đây là những đánh giá chi tiết về sản phẩm này.\n\n**Thiết kế tinh tế và hiện đại**\n\nBàn phím AULA F75 được thiết kế với phong cách xám + đen gradient, mang đến vẻ ngoài cực kỳ hiện đại và thời thượng. Sự kết hợp màu sắc này không chỉ tạo điểm nhấn mà còn dễ dàng hòa hợp với môi trường làm việc hay không gian gaming của bạn. Kích thước bàn phím hợp lý, phù hợp với các game thủ cần sự di chuyển nhanh chóng và linh hoạt.\n\n**Switch Reaper độc quyền**\n\nMột trong những điểm nổi bật của AULA F75 chính là hệ thống switch Reaper độc quyền. Đây là loại switch cơ học mang lại cảm giác gõ tuyệt vời với độ phản hồi cực nhạy. Người dùng sẽ cảm nhận được sự chính xác trong từng cú nhấn, đem lại lợi thế trong các trận đấu căng thẳng. Switch Reaper còn được thiết kế để có độ bền cao, giúp bàn phím hoạt động ổn định trong thời gian dài.\n\n**Chế độ kết nối đa dạng**\n\nBàn phím AULA F75 hỗ trợ nhiều chế độ kết nối, bao gồm kết nối có dây và không dây. Điều này mang lại sự linh hoạt cho người dùng khi có thể chọn cách kết nối phù hợp với nhu cầu sử dụng. Kết nối không dây tiện lợi giúp giảm thiểu sự rối rắm của dây cáp, cho phép bạn tận hưởng trải nghiệm chơi game mà không bị gò bó.\n\n**Đèn nền RGB tùy chỉnh**\n\nAULA F75 được trang bị hệ thống đèn nền RGB có thể tùy chỉnh theo nhiều chế độ khác nhau. Bạn có thể thay đổi màu sắc và hiệu ứng ánh sáng dễ dàng, tạo nên không gian gaming sống động và đầy màu sắc. Tính năng này không chỉ làm cho bàn phím trở nên nổi bật hơn mà còn giúp bạn dễ dàng sử dụng trong điều kiện ánh sáng yếu.\n\n**Tính năng chống khối phím**\n\nMột ưu điểm không thể bỏ qua của AULA F75 là tính năng chống khối phím (N-Key Rollover). Điều này cho phép bạn nhấn nhiều phím cùng một lúc mà không gặp phải vấn đề về phản hồi. Tính năng này cực kỳ hữu ích trong các trò chơi đòi hỏi sự phối hợp phím nhanh chóng và chính xác.\n\n**Tính năng hỗ trợ đa dạng**\n\nBàn phím AULA F75 cũng được trang bị những tính năng hỗ trợ đa dạng khác như khả năng lập trình macro, giúp bạn dễ dàng tạo ra các phím tắt cho các chức năng mà bạn thường xuyên sử dụng. Điều này không chỉ giúp tăng hiệu suất làm việc mà còn đem lại cảm giác thoải mái hơn khi chơi game.\n\n**Tổng kết**\n\nBàn phím AULA F75 là một sản phẩm đáng để sở hữu đối với bất kỳ game thủ hay người dùng công nghệ nào. Với thiết kế tinh tế, switch Reaper độc quyền, chế độ kết nối đa dạng cùng nhiều tính năng nổi bật khác, AULA F75 mang đến một trải nghiệm sử dụng tuyệt vời. Đây chính là một sự đầu tư tuyệt vời cho không gian chơi game và làm việc của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/aula-f75-ban-phim-co-gaming-3-mo__16_-photoroom_b9bf3a52b87e4c7f9e7af2d262b472f7.png'
    );
