INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F75 (Xanh nhạt + Trắng + Xanh đậm/ Grey Wood V3 Switch) F75BWG', 'ban-phim-aula-f75-xanh-nhat-trang-xanh-dam-grey-wood-v3-switch', 22,
        '{"Tên sản phẩm":"Bàn phím cơ gaming có dây AULA F75","Độ bền":"60 triệu lần bấm","Kết nối":"Có dây Type-C","Màu sắc":"2 màu đen và trắng","Đèn nền":"Led nhiều màu / 20 hiệu ứng ánh sáng","Loại switch":"Red switch","Hiệu ứng âm thanh":"Linear","Hot-Swap":"5 pin","Núm xoay đa chức năng":"Có","Mạch":"Ngược","Số lượng phím":80,"Điện áp/Dòng điện định mức":"DC5V-500mA","Kích thước (Dài x Rộng x Cao)":"322.7 × 143.2 × 43.1 mm ±1","Trọng lượng":"Khoảng 952 g (bao gồm cả cáp)","Tốc độ phản hồi":"1000Hz","Hệ điều hành tương thích":"WINXP, WIN7, WIN8, WIN10 (Driver có thể được yêu cầu trên hệ thống Win 95 và Win98)","Phụ kiện kèm theo":"Sách hướng dẫn sử dụng + Dây USB type-C"}',
        'Bàn phím AULA F75 được thiết kế với bộ khung chắc chắn, tạo cảm giác vững chãi khi sử dụng. Các phím bấm được làm từ chất liệu ABS chất lượng cao, đem lại cảm giác bền bỉ và chắc chắn. Màu sắc phối hợp hài hòa giữa xanh nhạt, trắng và xanh đậm tạo nên vẻ đẹp thời thượng, thu hút ánh nhìn ngay từ lần đầu tiên. \n\nTính năng chuyển đổi chế độ 3 trong 1\nMột trong những tính năng nổi bật nhất của bàn phím AULA F75 là khả năng chuyển đổi giữa 3 chế độ kết nối: USB, Bluetooth và Wireless 2.4G. Điều này mang lại sự tiện lợi cao cho người dùng, cho phép dễ dàng kết nối bàn phím máy tính với nhiều thiết bị khác nhau như máy tính cá nhân, laptop, hoặc thậm chí là các thiết bị game console. \n\nCông nghệ switch Grey Wood V3 cho cảm giác gõ tuyệt vời\nBàn phím AULA F75 sử dụng switch Grey Wood V3, nổi bật với khả năng phản hồi nhanh và cảm giác gõ dễ chịu. Các phím bấm được thiết kế với hành trình ngắn, giúp giảm thiểu mệt mỏi khi gõ lâu dài. Công nghệ này còn giúp tăng độ chính xác trong từng cú bấm, là một lợi thế lớn trong các trận đấu game đòi hỏi sự chính xác tuyệt đối.\n\nĐèn nền LED RGB tùy chỉnh\nVới hệ thống đèn LED RGB, người dùng có thể tùy chỉnh màu sắc và hiệu ứng ánh sáng theo ý thích. Điều này không chỉ giúp bàn phím AULA F75 trở nên bắt mắt hơn mà còn tạo ra không gian chơi game sống động, cho phép người dùng lựa chọn theo phong cách riêng của mình. Hệ thống ánh sáng còn giúp dễ dàng sử dụng trong điều kiện ánh sáng yếu.\n\nBề mặt chống trơn trượt và các phím chức năng thông minh\nBàn phím AULA F75 được thiết kế với bề mặt chống trơn trượt, giúp ổn định trong suốt quá trình sử dụng. Ngoài ra, các phím chức năng thông minh cho phép người dùng truy cập nhanh đến các tính năng như chỉnh âm lượng, chuyển chế độ đèn nền, và các macro phím, giúp tối ưu hóa trải nghiệm chơi game.',
        1, 'https://product.hstatic.net/200000722513/product/aula-f75-ban-phim-co-gaming-3-mo-photoroom_7494ea07e0244db7841ccf6e003ee374.png'
    );
