INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F75 Glacier Blue (Xanh nhạt + Trắng + Tím đậm /Ice Vein Switch) F75IBI', 'ban-phim-aula-f75-glacier-blue-xanh-nhat-trang-xanh-dam-ice-vein-switch', 22,
        '{"Tên sản phẩm":"Bàn phím cơ không dây AULA F75 Ice Blue","Loại bàn phím":"Tenkeyless 75%","Số phím":80,"Keycap":"Nhựa PBT","Phím đặc biệt":"Phím tăng giảm âm lượng/độ sáng của đèn","Tương thích":"WINXP, WIN7, WIN8, WIN10, Android, iOS, MAC","Hiệu ứng âm thanh":"Linear","Kết nối":"3 mode có dây Type-C & không dây 2.4G & Bluetooth","Độ dài dây":"1.6 m","Đèn LED":"LED RGB 16.8 triệu màu, 16 loại hiệu ứng","Pin":"Pin lithium 4000mAh có thể sạc lại, thời gian sạc khoảng 4 tiếng","Thời gian dùng":"Khoảng 30.5 giờ (hiệu ứng ánh sáng mặc định), khoảng 80 giờ (tắt LED)","Tiện ích":"Hot-Swap 5 pin, Gasket mount, Núm xoay đa chức năng, Màn hình hiển thị thông tin và hình ảnh, Mạch xuôi, Thiết kế cấu trúc gasket với sáu lớp vật liệu cách âm, Điện áp/Dòng sạc: DC 5V-1050mA, Điện áp định mức: DC 3.7V (đầy đủ 4.2V), 5 lớp giảm tiếng ồn, Cấu trúc đệm Leaf Spring, Tấm định vị PC có rãnh, Miếng đệm cách âm PET, Bông giảm thanh PORON, Phím đơn PCB có rãnh 1.2mm","Chất liệu":"Khuôn silicone ở dưới cùng","Cổng sạc":"Dây sạc Type-C","Kích thước":"32.27 × 14.32 × 4.31 cm","Khối lượng":"Khoảng 975g (không có cáp/bộ thu)/1023g (với cáp/bộ thu)","Hãng sản xuất":"Aula"}',
        'Aula F75 Glacier Blue không chỉ là chiếc bàn phím thông thường mà còn là một điểm nhấn nổi bật trên bàn làm việc của khách hàng. Các thông tin chi tiết về mẫu bàn phím Aula này sẽ được trình bày rõ nét trong những phần dưới đây.\n\nThao tác chuẩn xác với đèn LED hiện đại\nBàn phím không dây Aula F75 Glacier Blue sở hữu hệ thống đèn LED RGB cao cấp với 16.8 triệu màu. Nhờ đó, người dùng có thể soạn thảo văn bản chuẩn xác, làm việc và chơi game thoải mái trong điều kiện thiếu sáng.\n\nThiết kế bàn phím không dây Aula F75 Glacier Blue\nSản phẩm có gam màu xanh Glacier Blue dịu nhẹ, kết hợp với đèn LED hiện đại mang lại vẻ đẹp năng động và thời thượng cho không gian sáng tạo của khách hàng. Kiểu bàn phím Tenkeyless 80 phím giúp tiết kiệm diện tích bàn làm việc, thuận tiện để bố trí thêm chuột, miếng lót tay.\n\nTrải nghiệm gõ mượt mà với Ice Vein switch\nPhần keycap của bàn phím không dây Aula F75 Glacier Blue làm từ nhựa PBT cao cấp, đảm bảo sự cứng cáp, đem đến trải nghiệm gõ phím chắc tay. Thêm vào đó, các ký tự trên keycap được ép phun theo phương pháp Double-Shot hiện đại. Nhờ đó, keycap có độ bền cao, hạn chế tình trạng bong tróc khi sử dụng trong thời gian dài.\n\nSwitch bàn phím không dây Aula F75 Glacier Blue\nBàn phím được trang bị Ice Vein switch, mang lại hành trình gõ êm ái và mượt mà, giảm tiếng ồn hiệu quả. Điều này đặc biệt hữu ích khi người mua làm việc trong môi trường văn phòng mà không gây ảnh hưởng đến mọi người xung quanh.',
        1, 'https://product.hstatic.net/200000722513/product/aula-f75-ban-phim-co-gaming-phie__6_-photoroom_6d6bb2efd7174f3abb6bbd219083cea4.png'
    );
