INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AULA F87 PRO Black Gradient Grey Wood V4 Switch F8701', 'ban-phim-aula-f87-pro-black-gradient-grey-wood-v4-switch', 22,
        '{"Thương hiệu": "AULA", "Model": "F87 PRO", "Mã sản phẩm": "F8701", "Bố cục bàn phím": "87 phím, Tenkeyless (TKL)", "Kiểu kết nối": "Ba chế độ (USB-C, Bluetooth 5.0, 2.4GHz Wireless)", "Loại Switch": "AULA Custom V4 (Linear, Pre-lubed)", "Hỗ trợ Hotswap": "Có, Socket 5-pin", "Chất liệu Keycap": "PBT Double-shot", "Profile Keycap": "OEM", "Đèn nền": "RGB với nhiều hiệu ứng", "Dung lượng pin": "4000 mAh", "Chất liệu vỏ": "Nhựa cao cấp", "Màu sắc": "Đen Gradient Xám Gỗ", "Cấu trúc bên trong": "Gasket mount", "Tính năng bổ sung": "Foam tiêu âm nhiều lớp, Plate PC linh hoạt", "Anti-ghosting": "N-Key Rollover (NKRO)", "Tương thích hệ điều hành": "Windows, macOS, Linux", "Cáp kết nối": "USB-C sang USB-A có thể tháo rời"}',
        'Aula F87 Đen Gradient là lựa chọn đáng cân nhắc trong phân khúc bàn phím Gaming nhờ sự kết hợp giữa tính thẩm mỹ và độ bền cao. Với kiểu dáng hiện đại, tính năng tối ưu, dòng bàn phím này đang nhận được nhiều sự chú ý của người dùng yêu thích công nghệ.\n\nThiết kế hiện đại, đèn nền sinh động\nNgay từ cái nhìn đầu tiên, thiết kế Black Gradient của bàn phím không dây Aula F87 Đen Gradient đã mang đến cảm giác mạnh mẽ nhưng vẫn tinh tế. Cùng với đó là hệ thống đèn nền LED RGB tích hợp sẵn 16 hiệu ứng ánh sáng khác nhau, tạo nên không gian bàn phím sinh động và đầy cảm hứng. \n\nBàn phím không dây Aula F87 Đen Gradient\n\nBàn phím được thiết kế với 87 phím giúp tiết kiệm diện tích, đồng thời đảm bảo đầy đủ chức năng cần thiết. Phần thân thiết bị được hoàn thiện cứng cáp, kết hợp với form dáng tối ưu cho tư thế gõ thoải mái lâu dài.\n\nKeycap bền bỉ, switch gõ mượt mà\nVới keycap làm từ chất liệu PBT Double-Shot, bàn phím không dây Aula F87 Đen Gradient mang lại độ bền vượt trội và khả năng chống mờ chữ qua thời gian sử dụng. Hệ thống switch Grey Wood V4 cho phản hồi tuyến tính, hành trình phím sâu 4.0mm tạo nên cảm giác gõ mượt mà, giảm tiếng ồn hiệu quả. \n\nBàn phím không dây Aula F87 Đen Gradient\n\nBàn phím cũng hỗ trợ Hot-Swap 5 pin, cho phép người dùng thay đổi switch mà không cần hàn lại, nâng cao tính linh hoạt và cá nhân hóa. Đặc biệt, Gasket Mount và mạch xuôi cũng giúp tăng sự ổn định cho thiết bị khi thao tác, hạn chế rung lắc trong quá trình sử dụng.',
        1, 'https://product.hstatic.net/200000722513/product/aula-f87-pro-ban-phim-co-gaming-photoroom_a3b3b04fa0b74ad79a305e0b2e055979.png'
    );
