INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím AKKO 5098B Wasteland Survival V3 Piano Pro switch', 'ban-phim-akko-5098b-wasteland-survival-v3-piano-pro-switch', 4,
        '{"Tên sản phẩm":"Bàn phím cơ không dây AKKO 5098B Wasteland Survival (Layout 98%)","Layout":"98%","Kết nối":"Wireless 2.4GHz / Bluetooth 5.0 / Type A to Type C","Pin":"8000mAh, hỗ trợ sạc nhanh 2A, thời gian sử dụng kéo dài, sạc nhanh gấp 4 lần tiêu chuẩn","Màn hình LCD":"Hiển thị ảnh động GIF và thông tin hệ thống, tùy chỉnh qua Akko Cloud Driver","Tính năng":"N-Key Rollover, Winlock, Hotswap, Macro Akko V1.0","Chất liệu keycap":"PBT Dye-subbed – Cherry Profile","Kích thước":"409 × 138.4 × 32.3 mm ~ 1.02kg","Đèn nền":"RGB backlit, tùy chỉnh nhiều hiệu ứng ánh sáng","Switch":"AKKO V3 Piano Pro","Cấu trúc":"Gasket mounted","Hỗ trợ phần mềm":"AKKO Cloud Driver","Phụ kiện":"1 sách hướng dẫn sử dụng, 1 keypuller, 1 cover che bụi, 1 dây cáp USB","Tương thích":"Windows, MacOS, Linux"}',
        'AKKO 5098B Wasteland Survival gây ấn tượng mạnh mẽ ngay từ cái nhìn đầu tiên với thiết kế lấy cảm hứng từ vùng đất hoang tàn (Wasteland). Màu sắc chủ đạo là sự kết hợp giữa màu be cát, xám và các chi tiết màu cam, tạo nên vẻ ngoài bụi bặm, mạnh mẽ và đầy cá tính. Vỏ bàn phím được làm từ nhựa ABS cao cấp, mang lại độ bền cao và cảm giác chắc chắn khi sử dụng.\n\nLayout 98% (1800 Compact) của bàn phím máy tính này giúp tiết kiệm không gian bàn làm việc mà vẫn giữ đầy đủ các phím chức năng cần thiết, bao gồm cả cụm phím số. Thiết kế này đặc biệt phù hợp với những người dùng cần một bàn phím nhỏ gọn nhưng vẫn muốn có đầy đủ chức năng.\n\nBàn phím AKKO 5098B Wasteland Survival V3 Piano Pro switch\n\nKeycap PBT Dye-subbed Cherry Profile chất lượng cao\nAKKO 5098B Wasteland Survival được trang bị keycap PBT Dye-subbed với profile Cherry. Chất liệu PBT nổi tiếng với độ bền cao, chống mài mòn và không bị bóng sau thời gian dài sử dụng. Công nghệ in Dye-subbed đảm bảo các ký tự trên keycap sắc nét, không bị phai màu theo thời gian. Profile Cherry với độ cao vừa phải và đường cong ôm ngón tay mang lại cảm giác gõ phím thoải mái và chính xác.\n\nBàn phím AKKO 5098B Wasteland Survival V3 Piano Pro switchSwitch AKKO V3 Piano Pro cho trải nghiệm gõ phím mượt mà\nĐiểm nhấn của AKKO 5098B Wasteland Survival chính là switch AKKO V3 Piano Pro. Đây là loại switch linear (thẳng) với lực nhấn nhẹ, hành trình phím ngắn và độ nảy tốt, mang lại cảm giác gõ phím mượt mà, êm ái và nhanh chóng. Switch đã được lube sẵn từ nhà máy giúp giảm tiếng ồn và tăng độ mượt mà khi gõ.\n\nKhả năng hot-swap (thay nóng switch) cho phép người dùng dễ dàng tùy chỉnh switch theo sở thích cá nhân mà không cần phải hàn. Bàn phím hỗ trợ cả switch 3 pin và 5 pin, mang lại sự linh hoạt cao.\n\nKết nối 3 chế độ linh hoạt\nAKKO 5098B Wasteland Survival hỗ trợ 3 chế độ kết nối: Bluetooth 5.0, Wireless 2.4GHz và USB Type-C. Kết nối Bluetooth 5.0 cho phép kết nối không dây ổn định với nhiều thiết bị. Kết nối Wireless 2.4GHz với độ trễ thấp phù hợp cho việc chơi game. Kết nối USB Type-C đảm bảo tốc độ truyền tải dữ liệu nhanh chóng và ổn định.\n\nBàn phím AKKO 5098B Wasteland Survival V3 Piano Pro switch\n\nLED RGB và màn hình LCD tùy chỉnh\nBàn phím được trang bị hệ thống đèn nền RGB với nhiều hiệu ứng ánh sáng đẹp mắt, cho phép người dùng tùy chỉnh theo sở thích cá nhân. Màn hình LCD nhỏ được tích hợp trên bàn phím có thể hiển thị các thông tin như thời gian, ngày tháng, chế độ kết nối, mức pin và thậm chí là hình ảnh động tùy chỉnh.\n\nNgoài ra, với switch AKKO V3 Piano Pro, keycap PBT Cherry Profile và cấu trúc Gasket Mount, AKKO 5098B Wasteland Survival mang lại trải nghiệm gõ phím tuyệt vời. Cảm giác gõ phím mượt mà, êm ái và độ phản hồi tốt. Bàn phím cơ này phù hợp cho cả việc gõ văn bản và chơi game.',
        1, 'https://product.hstatic.net/200000722513/product/thiet-ke-chua-co-ten-5_64d4effa6b234296b868b9f29699334c.png'
    );
