INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Corsair K70 PRO TKL', 'ban-phim-corsair-k70-pro-tkl', 5,
        '{"Tên sản phẩm": "Corsair K70 PRO TKL", "Loại bàn phím": "Cơ học", "Form Factor": "Tenkeyless (TKL)", "Loại switch": "Cherry MX RGB Red", "Đèn nền": "RGB (từng phím)", "Tốc độ Polling Rate": "8000 Hz (Corsair AXON)", "Chất liệu Keycaps": "PBT Double-shot", "Chất liệu khung": "Nhôm Anodized", "Phần mềm điều khiển": "Corsair iCUE", "Kết nối": "USB Type-C (có thể tháo rời)", "Các phím chức năng đặc biệt": "Nút media chuyên dụng, con lăn âm lượng bằng nhôm, nút profile", "Chế độ giải đấu": "Có", "Kê tay": "Có thể tháo rời", "Trọng lượng": "930 g", "Kích thước (DxRxC)": "360 x 164 x 40 mm", "Hệ điều hành tương thích": "Windows", "Bộ nhớ trong": "Có"}',
        'Bàn phím Corsair K70 PRO TKL là một kiệt tác cơ khí, được chế tạo tỉ mỉ để mang đến trải nghiệm gõ phím đỉnh cao cho cả game thủ chuyên nghiệp lẫn người dùng văn phòng khó tính. Thiết kế Tenkeyless (TKL) tối ưu hóa không gian trên bàn làm việc, giữ nguyên các phím chức năng thiết yếu mà loại bỏ phần numpad rườm rà. Khung bàn phím được làm từ nhôm chải phay xước cao cấp, không chỉ tăng cường độ bền bỉ mà còn tạo nên vẻ ngoài sang trọng, cứng cáp. Trang bị Switch cơ học Cherry MX RGB tốc độ cao, mỗi phím bấm đều mang lại phản hồi xúc giác mượt mà, độ chính xác tuyệt đối và độ bền vượt trội lên đến 100 triệu lần nhấn. Công nghệ Corsair AXON mang đến tốc độ truyền tín hiệu siêu nhanh 8.000Hz Hyper-processing, giúp mọi thao tác của bạn được ghi nhận gần như tức thời, loại bỏ hoàn toàn độ trễ. Hệ thống đèn LED RGB mỗi phím với hàng triệu màu sắc tùy chỉnh, có thể đồng bộ hóa với các thiết bị Corsair khác thông qua phần mềm iCUE mạnh mẽ, tạo nên một không gian gaming hoặc làm việc đầy ấn tượng và cá tính. Các phím media chuyên dụng và con lăn âm lượng cho phép điều chỉnh nhạc và âm thanh nhanh chóng mà không cần rời khỏi màn hình. Bàn phím còn được trang bị dây cáp USB Type-C có thể tháo rời, tiện lợi cho việc di chuyển và lưu trữ. Corsair K70 PRO TKL không chỉ là một công cụ, mà còn là một tuyên ngôn về hiệu suất, phong cách và sự bền bỉ.',
        1, 'https://product.hstatic.net/200000722513/product/ban_phim_corsair_k70_pro_tkl_-_7_a6120ccfed004ef6a3ad11eee6842189.png'
    );
