INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Durgod MK 75 White Red Switch', 'ban-phim-durgod-mk-75-white-red-switch', 26,
        '{"Tên sản phẩm":"Durgod MK 75 White Red Switch","Kích thước":"75%","Số phím":82,"Pin":"3000mAh","Kết nối":"Không dây (Tri-Mode Wireless)","Layout":"ANSI","Switch":"Huano Blue, Huano Brown, Huano Red","Keycaps":"Double-Shot PBT","Hỗ trợ Hot-swap":"Yes","Đèn nền":"RGB","Thương hiệu":"Durgod","Trọng lượng":"917g","Kích thước tổng thể":"41.6 × 22.6 × 9 cm","Phím xoay (Knob)":"Yes","Shine-through Keycaps":"No","Model":"MK75 White"}',
        'Bàn phím Durgod MK75 White Red Switch là một lựa chọn tuyệt vời cho cả game thủ lẫn người dùng văn phòng, mang đến sự kết hợp hoàn hảo giữa hiệu suất, độ bền và thẩm mỹ. Thiết kế full-size (104 phím) cung cấp đầy đủ các phím chức năng, khu vực phím số và các phím điều hướng, đáp ứng mọi nhu cầu sử dụng, từ chơi game căng thẳng đến nhập liệu chi tiết. Phiên bản màu trắng tinh khôi mang lại vẻ ngoài sang trọng, hiện đại, dễ dàng hòa hợp với mọi không gian làm việc hay giải trí.\n\nĐiểm nhấn chính của Durgod MK75 nằm ở việc sử dụng switch Cherry MX Red. Cherry MX Red nổi tiếng với hành trình phím tuyến tính, lực nhấn nhẹ nhàng (45cN) và không có khấc tactile hay âm clicky. Điều này giúp mang lại trải nghiệm gõ phím mượt mà, nhanh nhạy, giảm thiểu tiếng ồn, lý tưởng cho việc chơi game đòi hỏi tốc độ phản hồi cao và đặc biệt phù hợp cho những ai thường xuyên gõ văn bản trong môi trường yên tĩnh hoặc làm việc nhiều giờ liền mà không gây mỏi tay. Độ bền của switch Cherry MX được đánh giá rất cao, lên đến 50 triệu lần nhấn, đảm bảo bàn phím sẽ đồng hành cùng bạn trong một thời gian dài.\n\nBàn phím Durgod MK75 còn được trang bị keycaps PBT chất lượng cao, được sản xuất bằng phương pháp double-shot. Chất liệu PBT có độ bền vượt trội, chống bóng mòn, chống bám vân tay và mang lại cảm giác gõ chắc chắn, cao cấp hơn so với ABS thông thường. Font chữ trên keycap sắc nét, không bị phai theo thời gian nhờ công nghệ double-shot. Nền bàn phím màu trắng kết hợp với keycap màu trắng và chữ màu đen hoặc xám tạo nên sự tương phản rõ ràng, dễ nhìn.\n\nNgoài ra, Durgod MK75 còn hỗ trợ phần mềm tùy chỉnh mạnh mẽ, cho phép người dùng thiết lập macro, tùy chỉnh chức năng các phím và điều chỉnh đèn nền (nếu có phiên bản LED). Khả năng N-Key Rollover (NKRO) đảm bảo mọi tổ hợp phím phức tạp đều được nhận diện chính xác, không bỏ sót bất kỳ lệnh nào trong những tình huống game đòi hỏi thao tác nhanh và đa điểm. Dây cáp USB có thể tháo rời, tiện lợi cho việc di chuyển và cất giữ. Với chất lượng hoàn thiện cao cấp, vật liệu bền bỉ và hiệu suất gõ phím vượt trội, Durgod MK75 White Red Switch xứng đáng là một khoản đầu tư xứng đáng cho trải nghiệm máy tính của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/ban_phim_durgod_mk75_white_-_5_ff3000c3feb448aab003753c39c38c2f.png'
    );
