INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím Durgod MK 75 White Brown Switch', 'ban-phim-durgod-mk-75-white-brown-switch', 26,
        '{"Tên sản phẩm":"Durgod MK 75 White Brown Switch","Kích thước":"75%","Số phím":82,"Pin":"3000mAh","Kết nối":"Không dây (Tri-Mode Wireless)","Layout":"ANSI","Switch":"Huano Blue, Huano Brown, Huano Red","Keycaps":"Double-Shot PBT","Hỗ trợ Hot-swap":"Yes","Đèn nền":"RGB","Thương hiệu":"Durgod","Trọng lượng":"917g","Kích thước tổng thể":"41.6 × 22.6 × 9 cm","Phím xoay (Knob)":"Yes","Shine-through Keycaps":"No","Model":"MK75 White"}',
        'Bàn phím Durgod MK 75 White Brown Switch là sự kết hợp hoàn hảo giữa thiết kế tinh tế và hiệu năng vượt trội, mang đến trải nghiệm gõ phím đỉnh cao cho cả game thủ lẫn người dùng văn phòng. Với tông màu trắng chủ đạo, bàn phím tạo cảm giác sang trọng, hiện đại và dễ dàng hòa hợp với mọi không gian làm việc hay giải trí. Chất liệu vỏ phím cao cấp không chỉ đảm bảo độ bền bỉ mà còn mang lại cảm giác cầm nắm chắc chắn, thoải mái. Điểm nhấn quan trọng nhất của Durgod MK 75 nằm ở việc sử dụng switch Cherry MX Brown. Loại switch này nổi tiếng với hành trình phím có khấc tactile, mang lại phản hồi xúc giác rõ ràng mà không gây ra tiếng ồn quá lớn, lý tưởng cho những ai cần sự cân bằng giữa cảm giác gõ và âm thanh dễ chịu. Điều này đặc biệt hữu ích khi bạn sử dụng bàn phím trong môi trường yên tĩnh hoặc cần tập trung cao độ. Bố cục 75% của bàn phím giúp tối ưu hóa không gian trên bàn làm việc bằng cách loại bỏ khu vực phím số riêng biệt và các phím chức năng không cần thiết, nhưng vẫn giữ lại các phím mũi tên và cụm phím điều hướng quan trọng, mang lại sự tiện lợi tối đa. Keycaps PBT chất lượng cao với quy trình double-shot đảm bảo ký tự không bị phai mờ theo thời gian, đồng thời mang lại bề mặt nhám mịn, chống bám vân tay hiệu quả. Bàn phím còn được trang bị đèn nền LED trắng đơn sắc, có thể điều chỉnh độ sáng và hiệu ứng, giúp bạn dễ dàng sử dụng trong điều kiện thiếu sáng và tăng thêm tính thẩm mỹ. Khả năng kết nối đa dạng, thường bao gồm cả có dây USB-C tiện lợi, cho phép truyền dữ liệu nhanh chóng và ổn định. Phần mềm đi kèm (nếu có) cho phép tùy chỉnh macro, remapping phím và các cài đặt nâng cao khác, mang đến khả năng cá nhân hóa mạnh mẽ. Durgod MK 75 White Brown Switch không chỉ là một công cụ gõ phím mà còn là một tuyên bố về phong cách và sự chú trọng đến chất lượng.',
        1, 'https://product.hstatic.net/200000722513/product/ban_phim_durgod_mk75_white_-_5_3d516d7d24f146d0acda23a8997e4392.png'
    );
