INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím E-Dra EK312 Beta Brown Switch', 'ban-phim-e-dra-ek312-beta-brown-switch', 23,
        '{"Tên sản phẩm": "Bàn phím E-Dra EK312 Beta Brown Switch", "Mã sản phẩm": "EK312", "Loại bàn phím": "Bàn phím cơ", "Loại switch": "E-Dra Beta Brown Switch", "Số lượng phím": 87, "Bố cục": "Tenkeyless (TKL)", "Đèn nền": "LED RGB", "Kết nối": "USB Type-C, Bluetooth 5.0, Wireless 2.4GHz", "Hỗ trợ NKRO": "Có", "Chất liệu keycap": "PBT Doubleshot", "Hỗ trợ phần mềm": "Có", "Kích thước": "359 x 136 x 36 mm", "Trọng lượng": "950g", "Tuổi thọ switch": "50 triệu lần nhấn", "Tương thích hệ điều hành": "Windows, macOS, Linux, Android, iOS", "Dung lượng pin": "3000 mAh", "Hot-swappable": "Có (5 pin)"}',
        'E-Dra EK312 là một kiệt tác mới từ thương hiệu nổi tiếng với thiết kế hiện đại và chất lượng vượt trội. Hãy khám phá những đặc điểm nổi bật của chiếc bàn phím này:\n\n\n\nSwitch E-Dra Mới Nhất -Hiệu Suất Gõ Tuyệt Vời\nE-Dra EK312 là đại diện xuất sắc của dòng sản phẩm bàn phím cơ với switch E-Dra mới nhất. Được trang bị ba loại switch cơ bản: Blue cho cảm giác click rõ ràng, Brown cho sự êm dịu và Red cho cảm giác nhẹ nhàng. Switch mới có stem vuông, chống bụi hiệu quả và mang lại độ chắc chắn, giảm rung lắc khi gõ. Đây được đánh giá là chất lượng phím bấm tốt nhất và mượt mà nhất trong phân khúc giá.\n\nThiết Kế Hiện Đại và Sang Trọng\nVới thiết kế hoàn toàn khác biệt,E-Dra EK312 là điểm nhấn nổi bật với phần logo E-Dra được in trên tấm nhựa trong suốt, tạo điểm nhấn hiện đại và phong cách. Vỏ phím thấp, với tầm nền đính switch lộ rõ, mang đến vẻ đẹp mạnh mẽ và hiện đại. Phần trên cùng được làm từ kim loại với các phiên bản màu đen, hồng phay xước và màu trắng nhám bạc, tạo độ hoàn thiện cao.\n\nKeycap ABS Double Shot - Sự Bền Bỉ Tối Ưu\nBộ keycap ABS Double Shot của EK312 không chỉ đảm bảo cảm giác gõ tốt mà còn nâng cao độ bền. Công nghệ in Double Shot (2 lớp) giúp keycap không bị mòn sau thời gian sử dụng. Layout chuẩn ANSI cung cấp sự thuận tiện khi muốn thay thế keycap.\n\n\n\nLED Rainbow Sáng Đều\nDù chỉ có LED Rainbow, nhưng E-Dra EK312 vẫn làm nổi bật với độ sáng cao và phân màu đẹp mắt. Mỗi phím chiếu sáng đều, tạo ra không gian làm việc lung linh và thuận tiện trong điều kiện thiếu sáng.\n\nKết Cấu Phím Chắc Chắn, Bền Bỉ\nMặc dù vỏ phím mỏng, nhưng chất lượng build của EK312 là đỉnh cao. Vỏ được gia cố chắc chắn, đảm bảo độ bền và sự ổn định. Chi tiết như stabilizer được điều chỉnh kỹ lưỡng, mang lại cảm giác gõ chắc chắn và không bị lọc xọc.\n\n\n\nE-Dra EK312 không chỉ là chiếc bàn phím cơ đơn thuần, mà là một tác phẩm nghệ thuật kết hợp giữa thiết kế và chất lượng vượt trội.\n\nBàn phím cơ gaming E-DRA là một sự lựa chọn phổ biến trong cộng đồng game thủ, và chúng có một số lợi ích quan trọng mà họ mang lại. Dưới đây là một số lợi ích của bàn phím cơ gaming E-DRA:\n\nTích hợp công nghệ cơ học: Bàn phím E-DRA sử dụng công nghệ cơ học cho các switch, giúp cải thiện độ nhạy bén và độ chính xác khi gõ phím và chơi game. Điều này giúp bạn có trải nghiệm gõ phím tốt hơn và nâng cao hiệu suất trong game.\n\nĐộ bền cao: Bàn phím E-DRA được xây dựng để chịu được nhiều lần nhấn phím liên tục mà không bị hỏng hóc. Điều này đặc biệt quan trọng trong các trò chơi yêu cầu tốc độ và phản xạ nhanh nhạy, nơi bạn thường phải nhấn phím mạnh.\n\nĐèn nền RGB: Hầu hết các bàn phím E-DRA gaming đi kèm với tính năng đèn nền RGB, cho phép bạn tùy chỉnh ánh sáng theo ý muốn. Điều này không chỉ tạo ra một hình ảnh hấp dẫn trên bàn làm việc của bạn mà còn giúp bạn dễ dàng nhận biết và gán phím theo màu sắc trong game.\n\nCác phím tùy chỉnh: Một số bàn phím E-DRA có các phím tùy chỉnh dành riêng cho game thủ, cho phép bạn lập trình các phím macro và thiết lập nhanh chóng trong game. Điều này giúp tối ưu hóa hiệu suất chơi game của bạn.\n\nPhần mềm hỗ trợ: E-DRA cung cấp phần mềm hỗ trợ mạnh mẽ cho các bàn phím của họ, cho phép bạn tùy chỉnh cài đặt, lưu trữ các hồ sơ cài đặt và theo dõi hiệu suất của bàn phím. Điều này giúp bạn dễ dàng quản lý và tối ưu hóa trải nghiệm sử dụng.\n\nThiết kế chất lượng: Bàn phím E-DRA thường được thiết kế với chất liệu và bố cục chất lượng cao, mang lại sự thoải mái và sự chắc chắn khi sử dụng.',
        1, 'https://product.hstatic.net/200000722513/product/478_z4737225268402_8c6b931cc6bc7_8038c8fd189c46569d08719cb0073477.png'
    );
