INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím DAREU EK98 PRO Proto Dream switch', 'ban-phim-dareu-ek98-pro-proto-dream-switch', 25,
        '{"Model": "DAREU EK98 PRO", "Layout": "98%", "Switch": "Proto Dream switch", "Keycap Material": "PBT Doubleshot", "Keycap Profile": "OEM", "Connectivity": "USB Type-C, Bluetooth 5.0, 2.4GHz Wireless", "Backlighting": "RGB (18 chế độ)", "Hot-swappable": "Có (hỗ trợ switch 3/5 pin)", "N-Key Rollover": "Toàn bộ phím (Full N-key rollover)", "Polling Rate": "1000Hz (chế độ có dây)", "Mounting Style": "Gasket Mount", "Sound Dampening": "Foam tiêu âm EVA + IXPE", "Build Material": "Vỏ nhựa ABS", "Battery Capacity": "3000mAh", "Dimensions": "400 x 140 x 38mm", "Weight": "1100g", "Software": "DAREU Cloud Driver", "Cable Type": "Dây cáp Type-C có thể tháo rời", "Stabilizer": "Plate-mounted, pre-lubed"}',
        'Bàn phím DAREU EK98 PRO Proto Dream Switch là một kiệt tác cơ học dành cho game thủ và người dùng chuyên nghiệp, mang đến trải nghiệm gõ phím đỉnh cao với sự kết hợp hoàn hảo giữa hiệu suất, thẩm mỹ và công nghệ tiên tiến. Sở hữu layout 98 phím tiện lợi, EK98 PRO tối ưu không gian làm việc của bạn mà vẫn giữ trọn vẹn các phím chức năng và cụm phím số, phù hợp cho cả công việc văn phòng lẫn chiến game. Điểm nhấn đặc biệt của sản phẩm nằm ở bộ switch Proto Dream độc quyền từ DAREU, được thiết kế để mang lại cảm giác nhấn êm ái, mượt mà, độ phản hồi nhanh nhạy và độ bền vượt trội, giảm thiểu tối đa tiếng ồn khi gõ, tạo không gian làm việc tập trung hơn. Vỏ case được chế tác từ hợp kim nhôm cao cấp, mang lại sự chắc chắn, sang trọng và cảm giác đầm tay khi sử dụng. Keycap PBT double-shot với độ bền màu cao, chống bám vân tay và mài mòn hiệu quả, giữ cho bàn phím luôn như mới sau thời gian dài sử dụng. Hệ thống đèn LED RGB với nhiều chế độ tùy chỉnh sống động, cho phép bạn cá nhân hóa không gian giải trí hoặc làm việc theo phong cách riêng. DAREU EK98 PRO còn hỗ trợ kết nối đa dạng, bao gồm có dây USB-C, Bluetooth 5.0 và Wireless 2.4GHz, mang đến sự linh hoạt tối đa cho mọi nhu cầu sử dụng. Phần mềm đi kèm cho phép tùy chỉnh sâu về macro, keymap và hiệu ứng ánh sáng, biến bàn phím thành công cụ đắc lực hỗ trợ bạn chinh phục mọi thử thách.',
        1, 'https://product.hstatic.net/200000722513/product/ek98_pro_proto__5__a05b4255ca454_bdf91c5567f5439384cad85937b61a86.png'
    );
