INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím cơ DareU EK65s White Black PBT Dream Switch', 'ban-phim-co-dareu-ek65s-white-black-pbt-dream-switch', 25,
        '{"Tên sản phẩm": "Bàn phím cơ DareU EK65s White Black PBT Dream Switch", "Thương hiệu": "DareU", "Model": "EK65s", "Loại bàn phím": "Cơ học", "Kiểu kết nối": "Có dây (USB Type-C)", "Loại Switch": "DareU Dream Switch (Linear, Pre-lubed)", "Chất liệu Keycap": "PBT Double-shot", "Bố cục phím": "65% (68 phím)", "Số lượng phím": 68, "Đèn nền": "LED RGB", "Tính năng đặc biệt": "Hot-swap, Gasket mount, Foam tiêu âm", "Tuổi thọ phím": "50 triệu lần nhấn", "Tương thích hệ điều hành": "Windows, macOS", "Màu sắc": "Trắng đen"}',
        'Bàn phím cơ DareU EK65S White Black RGB là mẫu keyboard nhỏ gọn, được thiết kế để tối ưu hóa không gian làm việc mà vẫn giữ được vẻ đẹp và tính năng mạnh mẽ. Nhờ keycap PBT Double-shot bền bỉ và switch Dream độc quyền, DareU EK65S White Black RGB đảm bảo mang tới cảm giác gõ tuyệt vời trong suốt quá trình sử dụng.\n\nThiết kế nhỏ gọn, tinh tế, phù hợp với mọi không gian làm việc\nBàn phím cơ DareU EK65S White Black RGB gây ấn tượng mạnh với thiết kế nhỏ gọn và tinh tế. Với chỉ 68 phím, dòng bàn phím DareU này giúp tiết kiệm không gian tối ưu, cho phép người dùng dễ dàng bố trí trên bàn làm việc. Kèm theo đó là tông màu trắng đen thanh lịch, phù hợp với mọi phong cách nội thất, từ tối giản cho tới hiện đại.\n\nThiết kế bàn phím cơ DareU EK65S White Black RGB\n\nKeycap PBT Double-shot bền bỉ cùng Switch Dream độc quyền\nKeycap PBT Double-shot của DareU EK65S White Black RGB là thế hệ keycap mới, đem tới lại cảm giác gõ phím mượt mà và cực kỳ bền bỉ. Cụ thể, chất liệu PBT có độ bền cao, chống mài mòn tốt, giúp các phím không bị mờ hay mòn theo thời gian. Kèm theo đó là switch Dream độc quyền trên bàn phím cũng được đánh giá cao khi mang đến trải nghiệm gõ tuyệt vời với phản hồi nhanh nhạy, cảm giác mềm mại nhưng vẫn rất chắc chắn.',
        1, 'https://product.hstatic.net/200000722513/product/ek65s__1__fc88e487086b434bb1e00f_ae5f290b5c754084afd20ded50db0397.png'
    );
