INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Laptop Dell Inspiron 3530 N5I5007W1-Silver', 'laptop-dell-inspiron-3530-n5i5007w1-silver', 6,
        '{"CPU": "Intel Core i5-1335U (3.4 GHz - 4.6 GHz / 12MB / 10 nhân, 12 luồng, 12 MB Intel® Smart Cache)", "RAM": "16GB (2 x 8GB) DDR4 2666MHz (2 khe SO-DIMM, hỗ trợ tối đa 16GB SDRAM)", "Ổ cứng": "512GB SSD NVMe PCIe (1 khe, có thể nâng cấp tối đa 2TB)", "Card đồ họa": "Intel Iris Xe Graphics (với bộ nhớ hai kênh), Intel® UHD Graphics", "Màn hình": "15.6 Inch FHD (1920 x 1080), 120Hz, 250 nits, WVA, chống chói, màn hình viền hẹp LED", "Cổng giao tiếp": "1 x USB 3.2 Type C, 1 x USB 3.2 Type A, 1 x USB 2.0, 1 x HDMI 1.4, 1 x Audio Combo Jack", "Bàn phím": "Bàn phím tiêu chuẩn, có phím số, hỗ trợ đèn nền đơn sắc", "Wireless": "Wi-Fi 6 (802.11ax)", "Bluetooth": "v5.2", "Webcam": "HD 720p", "Audio": "Realtek ALC3204, âm thanh Realtek", "Pin": "3 Cell 41Whr", "Hệ điều hành": "Windows 11 Home + Office Home&Student", "Trọng lượng": "1.83 kg", "Màu sắc": "Bạc", "Chất liệu": "Nhựa", "Kích thước": "358.5 x 235.56 x 16.96 mm"}',
        'Laptop Dell Inspiron 3530 N5I5007W1-Silver là một lựa chọn tuyệt vời cho người dùng cần một chiếc máy tính xách tay đáng tin cậy, hiệu năng ổn định và thiết kế hiện đại. Với tông màu Bạc (Silver) trang nhã, chiếc máy này không chỉ thu hút về mặt thẩm mỹ mà còn mang đến trải nghiệm sử dụng mượt mà cho các tác vụ hàng ngày. Máy được trang bị bộ vi xử lý Intel Core i5 thế hệ mới, đảm bảo khả năng xử lý đa nhiệm ấn tượng, từ lướt web, xem phim, làm việc văn phòng với các ứng dụng Office, đến các tác vụ đòi hỏi cấu hình cao hơn một chút như chỉnh sửa ảnh cơ bản hay lập trình. Màn hình kích thước lớn 15.6 inch mang lại không gian hiển thị rộng rãi, sắc nét, rất lý tưởng cho công việc và giải trí. Bàn phím full-size cùng với bàn rê chuột (touchpad) nhạy bén giúp thao tác nhập liệu và điều khiển trở nên dễ dàng và thoải mái. Thiết kế mỏng nhẹ và bền bỉ của dòng Inspiron giúp bạn dễ dàng mang theo bên mình trong các chuyến đi công tác hay di chuyển. Dung lượng lưu trữ đủ lớn giúp bạn thoải mái lưu trữ tài liệu, hình ảnh, video và các ứng dụng cần thiết. Kết nối đa dạng với các cổng USB, HDMI, khe đọc thẻ nhớ... cho phép bạn dễ dàng kết nối với các thiết bị ngoại vi khác. Dell Inspiron 3530 N5I5007W1-Silver là sự kết hợp hài hòa giữa hiệu năng, tính di động và phong cách, là trợ thủ đắc lực cho công việc và học tập.',
        1, 'https://product.hstatic.net/200000722513/product/ava_6b09726b720340cc98e16dd6808c96c7.png'
    );
