INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Laptop Dell Vostro 3530 V5I3465W1 Gray', 'laptop-dell-vostro-3530-v5i3465w1-gray', 6,
        '{"CPU": "Intel Core i3-1305U (3.3GHz~4.5GHz) 5 Cores 6 Threads", "RAM": "8GB (8x1) DDR4 3200MHz (2x SO-DIMM socket, up to 16GB SDRAM)", "Ổ cứng": "512GB SSD M.2 PCIE", "Card đồ hoạ": "Intel® UHD Graphics", "Màn hình": "15.6 inch FHD (1920 x 1080) 120Hz 250 nits WVA Anti-Glare LED Backlit Narrow Border Display", "Cổng kết nối": "1x USB 3.2 Gen 1 port, 1x USB 2.0 port, 1x USB 3.2 Gen 1 Type-C® port, 1x USB 3.2 Gen 1 Type-C® port with DisplayPort Alt Mode 1.4/Power Delivery (only for computers with aluminum chassis or NVIDIA® GeForce MX550 graphics card), 1x Universal audio port, 1x HDMI 1.4 port, 1x RJ45 Ethernet port, 1x Power-adapter port", "Audio": "Stereo speakers, 2 W x 2 = 4 W total", "Webcam": "720p at 30 fps HD camera, single-integrated microphone", "Đọc thẻ nhớ": "SD card slot", "Chuẩn WiFi": "Realtek Wi-Fi 5 RTL8821CE, 1x1, 802.11ac, MU-MIMO", "Bluetooth": "Bluetooth® wireless card v5.2", "Hệ điều hành": "Windows 11 Home + Office Home & Student 2021", "Pin": "3 Cell 41WHr", "Trọng lượng": "1.66 kg", "Màu sắc": "Xám", "Kích thước": "358.5 x 235.56 x 16.96 ~ 18.99 mm"}',
        'Laptop Dell Vostro 3530 V5I3465W1 Gray là một lựa chọn tuyệt vời cho người dùng doanh nghiệp và cá nhân đòi hỏi hiệu năng ổn định, thiết kế chuyên nghiệp và tính di động cao. Với tông màu xám (Gray) trang nhã, chiếc máy này không chỉ gây ấn tượng bởi vẻ ngoài hiện đại mà còn thể hiện sự tinh tế trong từng đường nét thiết kế. Màn hình 15.6 inch mang đến không gian làm việc rộng rãi, lý tưởng cho việc xử lý đa nhiệm, xem nội dung giải trí hay trình chiếu. Độ phân giải Full HD (hoặc tương đương tùy cấu hình chi tiết) đảm bảo hình ảnh sắc nét, màu sắc chân thực, giúp bạn làm việc và giải trí hiệu quả hơn.\n\nSức mạnh của Dell Vostro 3530 đến từ bộ vi xử lý Intel Core i3 thế hệ mới (hoặc tùy cấu hình cụ thể), kết hợp với dung lượng RAM đủ lớn để xử lý mượt mà các tác vụ văn phòng, duyệt web, xem phim và chạy các ứng dụng thông thường. Ổ cứng SSD dung lượng cao giúp tăng tốc độ khởi động máy, mở ứng dụng và truy xuất dữ liệu, mang lại trải nghiệm sử dụng nhanh chóng và liền mạch.\n\nBàn phím được thiết kế khoa học với khoảng cách phím hợp lý, hành trình phím sâu và độ nảy tốt, mang lại cảm giác gõ thoải mái và chính xác ngay cả khi làm việc trong thời gian dài. Touchpad rộng rãi, nhạy bén, hỗ trợ đa điểm, giúp bạn thao tác dễ dàng mà không cần đến chuột rời.\n\nVề kết nối, Dell Vostro 3530 được trang bị đầy đủ các cổng kết nối cần thiết như USB, HDMI, khe đọc thẻ nhớ, cổng âm thanh, Wi-Fi và Bluetooth mới nhất, đảm bảo khả năng kết nối linh hoạt với các thiết bị ngoại vi và mạng internet. Pin có thời lượng sử dụng đủ cho một ngày làm việc, giúp bạn yên tâm di chuyển mà không cần lo lắng về việc sạc pin thường xuyên.\n\nChiếc laptop này còn chú trọng đến bảo mật với các tính năng như tùy chọn cảm biến vân tay (tùy cấu hình), giúp bảo vệ dữ liệu cá nhân và doanh nghiệp của bạn một cách an toàn. Thiết kế mỏng nhẹ và bền bỉ theo tiêu chuẩn của Dell Vostro, bạn có thể tự tin mang máy theo bên mình đến bất cứ đâu và trong mọi môi trường làm việc. Laptop Dell Vostro 3530 V5I3465W1 Gray là một sự kết hợp hoàn hảo giữa hiệu năng, thiết kế và tính di động, đáp ứng tốt nhu cầu của người dùng trong công việc cũng như giải trí hàng ngày.',
        1, 'https://product.hstatic.net/200000722513/product/ava_323374780a104f079d493a9001f17921.png'
    );
