INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Laptop Dell Inspiron 15 3530 i5U085W11BLU', 'laptop-dell-inspiron-15-3530-i5u085w11blu', 6,
        '{"CPU": "Intel(R) Core(TM) i5-1335U Processor, 10 Cores 12 Threads (12MB Cache, up to 4.4 GHz)", "RAM": "1 x 8GB DDR4 2666MHz (2x SO-DIMM socket, up to 16GB SDRAM)", "Ổ cứng": "512GB SSD NVMe PCIe (1 Slot)", "Card đồ họa": "Intel Iris Xe Graphics (with dual channel memory), Intel® UHD Graphics", "Màn hình": "15.6 Inch FHD (1920 x 1080), 120Hz, Anti-Glare LED Backlit Narrow Border Display", "Wireless": "802.11ax 2x2 WiFi", "LAN": "10/100 Mbps", "Bluetooth": "v5.2", "Cổng giao tiếp": "2 x USB 3.2 Gen 1 ports (on systems configured with non Type-C®), 1 x USB 2.0 port, 1 x headset (headphone and microphone combo) port, 1 x HDMI 1.4 port, One SD card slot", "Webcam": "720p at 30 fps HD camera, single-integrated microphone", "Audio": "Realtek ALC3204, âm thanh Realtek", "Pin": "3-cell Li-ion, 41 Wh", "Hệ điều hành": "Windows 11 Home + Office Home&Student", "Kích thước": "358.5 x 235.56 x 16.96~18.99 mm", "Trọng lượng": "1.65 kg", "Màu sắc": "Carbon Black"}',
        'Laptop Dell Inspiron 15 3530 (i5U085W11BLU) là một lựa chọn tuyệt vời cho người dùng cần một chiếc laptop đa năng, hiệu năng ổn định và thiết kế hiện đại. Với bộ vi xử lý Intel Core i5 thế hệ mới, máy đáp ứng mượt mà các tác vụ văn phòng, học tập, giải trí và cả những công việc đòi hỏi cấu hình cao hơn một chút như chỉnh sửa ảnh cơ bản hay lập trình. Dung lượng RAM 8GB cho phép bạn làm việc đa nhiệm hiệu quả, mở nhiều ứng dụng cùng lúc mà không lo giật lag. Ổ cứng SSD dung lượng 256GB không chỉ mang đến tốc độ khởi động máy, mở ứng dụng nhanh chóng mà còn cung cấp không gian lưu trữ đủ dùng cho các tệp tin cá nhân và công việc quan trọng.\nMàn hình 15.6 inch, độ phân giải Full HD (1920x1080) mang đến hình ảnh sắc nét, chi tiết và màu sắc sống động, giúp bạn có trải nghiệm xem phim, làm việc và giải trí tốt hơn. Công nghệ chống lóa (anti-glare) trên màn hình giúp giảm thiểu phản xạ ánh sáng, bảo vệ mắt người dùng khi sử dụng trong thời gian dài hoặc ở những nơi có ánh sáng mạnh.\nThiết kế của Dell Inspiron 15 3530 (i5U085W11BLU) hướng đến sự tối giản và thanh lịch với tông màu xanh dương (BLU) độc đáo, tạo điểm nhấn cá tính. Lớp vỏ ngoài được hoàn thiện tỉ mỉ, mang lại cảm giác cao cấp và bền bỉ. Bàn phím chiclet với hành trình phím hợp lý và độ nảy tốt giúp thao tác gõ phím thoải mái và chính xác. Touchpad rộng rãi, nhạy bén hỗ trợ đa điểm, giúp bạn dễ dàng điều khiển con trỏ và thực hiện các thao tác vuốt, chạm.\nSản phẩm được cài sẵn hệ điều hành Windows 11 Home bản quyền, mang đến giao diện hiện đại, tính năng bảo mật nâng cao và trải nghiệm người dùng mượt mà. Các cổng kết nối đa dạng bao gồm USB 3.2, HDMI, khe đọc thẻ nhớ SD, jack cắm tai nghe 3.5mm, đáp ứng tốt nhu cầu kết nối với các thiết bị ngoại vi như chuột, bàn phím, màn hình ngoài, máy chiếu hay ổ cứng di động.\nLaptop Dell Inspiron 15 3530 (i5U085W11BLU) còn nổi bật với thời lượng pin ấn tượng, cho phép bạn làm việc, học tập hoặc giải trí trong nhiều giờ liên tục mà không cần cắm sạc, rất phù hợp với những người thường xuyên di chuyển. Đây là sự kết hợp hoàn hảo giữa hiệu năng, thiết kế và tính di động, là người bạn đồng hành đáng tin cậy cho mọi nhu cầu của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/inspiron-15-3530-i5u085w11blu_b60d09f21993480cadda384a5fe9c154.png'
    );
