INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Laptop Dell Inspiron 14 7420 1YT85', 'laptop-dell-inspiron-14-7420-1yt85', 6,
        '{"CPU": "Intel® Core™ i7-1255U upto 4.70GHz, 10 cores 12 threads, 12Mb Cache", "RAM": "16GB (2 x 8GB) DDR4 3200MHz (2x SO-DIMM socket, up to 16GB SDRAM)", "Ổ cứng": "512GB M.2 PCIe NVMe SSD", "Card đồ họa": "NVIDIA MX550 2GB GDDR6", "Màn hình": "14.0 inch FHD+ (1920 x 1200) 60Hz, Touch, WVA, TrueLife™, Narrow Border, Pen Support", "Wireless": "Intel® Wi-Fi 6E", "LAN": "None", "Bluetooth": "v5.2", "Cổng giao tiếp": "2 x USB 3.2 Gen 2x2 Type-C port with DisplayPort and Power Delivery, 1 x USB 3.2 Gen 1 Type-A, 1 x headset (headphone and microphone combo) port, 1 x HDMI 1.4 port, One SD card slot", "Webcam": "FHD Webcam", "Audio": "2 Loa", "Pin": "4-Cell Battery, 54WHr", "Hệ điều hành": "Windows 11 Home + Office Home&Student", "Kích thước": "314 x 227.5 x 17.9mm", "Trọng lượng": "1.57 kg", "Màu sắc": "Platinum Silver"}',
        'Laptop Dell Inspiron 14 7420 1YT85 là một cỗ máy mạnh mẽ và linh hoạt, được thiết kế để đáp ứng mọi nhu cầu từ công việc, học tập đến giải trí. Màn hình cảm ứng 14 inch sắc nét cho phép người dùng tương tác trực tiếp với các ứng dụng, tạo ra trải nghiệm sử dụng mượt mà và trực quan hơn. Bản lề xoay 360 độ cho phép chuyển đổi dễ dàng giữa các chế độ laptop, máy tính bảng, lều và đứng, mang lại sự tiện lợi tối đa trong mọi tình huống.\n\nSức mạnh xử lý của máy đến từ bộ vi xử lý Intel Core thế hệ mới, kết hợp với dung lượng RAM lớn, đảm bảo hiệu năng đa nhiệm ấn tượng, xử lý các tác vụ nặng một cách nhanh chóng và hiệu quả. Ổ cứng SSD dung lượng cao không chỉ cung cấp không gian lưu trữ rộng rãi cho dữ liệu cá nhân và ứng dụng mà còn giúp máy khởi động và tải ứng dụng chỉ trong chớp mắt.\n\nThiết kế mỏng nhẹ, sang trọng với vỏ kim loại cao cấp không chỉ tạo nên vẻ ngoài tinh tế mà còn đảm bảo độ bền bỉ theo thời gian. Bàn phím chiclet với hành trình phím hợp lý và đèn nền cho phép làm việc thoải mái ngay cả trong điều kiện ánh sáng yếu. Hệ thống âm thanh sống động mang đến trải nghiệm giải trí tuyệt vời.\n\nVới đầy đủ các cổng kết nối thông dụng, người dùng có thể dễ dàng kết nối với các thiết bị ngoại vi khác. Pin dung lượng lớn đảm bảo thời gian sử dụng bền bỉ, giúp bạn làm việc và giải trí suốt cả ngày mà không lo hết pin. Dell Inspiron 14 7420 1YT85 là sự lựa chọn hoàn hảo cho những ai tìm kiếm một chiếc laptop đa năng, hiệu năng cao, thiết kế đẹp và mang lại trải nghiệm người dùng vượt trội.',
        1, 'https://product.hstatic.net/200000722513/product/-laptop-dell-inspiron-14-7420-1yt85-1_8ea0fa1766bd44fe9d096443149a648c_87ea9790caa44b339b776bb3ba033e15.png'
    );
