INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Laptop Dell Latitude 3520 P108F001 70280538', 'laptop-dell-latitude-3520-p108f001-70280538', 6,
        '{"CPU": "Intel Core i7-1165G7 (2.58GHz~4.7GHz) 4 Cores 8 Threads", "RAM": "8GB (1x8) DDR4 3200MHz (2x SO-DIMM socket, up to 32GB SDRAM)", "Ổ cứng": "256GB SSD M.2 PCI-E", "Card đồ họa": "Intel Iris Xe Graphics (with dual channel memory)", "Màn hình": "15.6\" FHD (1920x1080), 60Hz, Non-Touch", "Cổng giao tiếp": "2 x USB 3.2 Gen1 Type-A, 1 x USB 3.2 Gen2x2 Type-C (Display Port) hỗ trợ Power Delivery, 1 x HDMI 1.4, 1 x RJ45, 1 x MicroSD Card reader, 1 x Audio jack", "Bàn phím": "Có dãy phím Numpad, Đèn nền bàn phím đơn sắc", "Khe đọc thẻ nhớ": "SD Media Card Reader", "Audio": "2x 2W Realtek", "Lan": "100/1000 Mbps", "Wifi + Bluetooth": "Wi-Fi 6 AX201 2x2 + v5.1", "Webcam": "HD Webcam", "Hệ điều hành": "Windows 11 Home", "Pin": "3 Cell 41WHr", "Trọng lượng": "1.79 kg", "Màu sắc": "Carbon Black", "Kích thước": "361 x 240.9 x 18.06mm"}',
        'Laptop Dell Latitude 3520 P108F001 70280538 là một lựa chọn lý tưởng cho các chuyên gia và người dùng doanh nghiệp, mang đến sự cân bằng hoàn hảo giữa hiệu năng, tính di động và độ bền. Với thiết kế mỏng nhẹ và hiện đại, chiếc laptop này dễ dàng đồng hành cùng bạn trong mọi hành trình công tác hay làm việc từ xa. Màn hình 15.6 inch sắc nét cung cấp không gian hiển thị rộng rãi, lý tưởng cho việc xử lý đa nhiệm, xem tài liệu, trình chiếu và giải trí. Cấu hình mạnh mẽ với bộ vi xử lý Intel Core tiên tiến, RAM dung lượng cao và ổ cứng SSD tốc độ cao đảm bảo khả năng vận hành mượt mà, nhanh chóng trên mọi ứng dụng, từ công việc văn phòng cơ bản đến các tác vụ đòi hỏi hiệu năng cao hơn. Bàn phím được thiết kế khoa học với hành trình phím tốt, mang lại trải nghiệm gõ phím thoải mái và chính xác, ngay cả khi làm việc trong thời gian dài. Touchpad nhạy bén, hỗ trợ đa điểm, giúp thao tác dễ dàng và tiện lợi. Laptop Dell Latitude 3520 được trang bị đầy đủ các cổng kết nối cần thiết, bao gồm USB, HDMI, khe cắm thẻ nhớ, giúp bạn dễ dàng kết nối với các thiết bị ngoại vi, màn hình ngoài hay truy xuất dữ liệu. Webcam tích hợp chất lượng cao cùng hệ thống micro thu âm tốt hỗ trợ tối đa cho các cuộc họp trực tuyến và giao tiếp từ xa. Pin dung lượng lớn cho thời gian sử dụng bền bỉ, giúp bạn làm việc độc lập mà không cần lo lắng về việc tìm kiếm nguồn sạc. Chất liệu vỏ máy cao cấp, đạt các tiêu chuẩn về độ bền quân đội, đảm bảo khả năng chống chịu tốt trước các tác động vật lý, mang lại sự yên tâm tuyệt đối khi sử dụng. Dell Latitude 3520 P108F001 70280538 không chỉ là một chiếc laptop hiệu năng cao mà còn là một người bạn đồng hành đáng tin cậy, hỗ trợ bạn tối đa trong công việc và cuộc sống.',
        1, 'https://product.hstatic.net/200000722513/product/latitude-3520-p108f001-70280538-fix_83b4c85f06d145199d87d838dc9eca04.png'
    );
