INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột ASUS ROG Strix Impact III Wireless', 'chuot-asus-rog-strix-impact-iii-wireless', 3,
        '{"Kết nối": "Bluetooth 5.1, RF 2.4GHz", "Loại pin": "Pin AA / AAA", "Thời lượng pin": "Lên đến 450 tiếng (không bật LED)", "Cảm biến": "ROG AimPoint", "Độ nhạy": "36000 DPI", "Switch chuột": "ROG 70M Mechanical Switch", "Tần số gửi tín hiệu chuột tới máy tính": "1000 Hz", "Kiểu chuột": "Claw grip, Fingertip grip", "Kích thước": "120(L) x 62(W) x 39(H) mm", "Trọng lượng không cáp": "57g (không bao gồm USB Dongle và pin), 68g (bao gồm pin AAA), 72g (bao gồm pin AA)", "Sẵn trong hộp": "1 x ROG Impact III Wireless gaming mouse, 1 x Wireless receiver, 1 x Quick start guide, 1 x Warranty booklet, 1 x AA battery, 1 x AAA battery, 1 x AAA to AA battery converter holder"}',
        'Chuột ASUS ROG Strix Impact III Wireless là một tuyệt phẩm công nghệ dành cho game thủ, được thiết kế để mang lại hiệu suất vượt trội và sự thoải mái tối đa. Với khả năng kết nối ba chế độ linh hoạt – 2.4GHz siêu nhanh, Bluetooth tiện lợi và chế độ có dây ổn định – người dùng có thể dễ dàng chuyển đổi tùy theo nhu cầu, đảm bảo trải nghiệm chơi game mượt mà, không gián đoạn. Trọng lượng siêu nhẹ chỉ 59 gram của Impact III Wireless giúp game thủ thực hiện các pha di chuyển chuột nhanh và chính xác mà không gây mỏi tay trong suốt thời gian dài sử dụng. Bên trong, chuột được trang bị cảm biến quang học ROG AimPoint tiên tiến với độ nhạy lên đến 36.000 DPI, cùng tốc độ theo dõi 650 IPS và gia tốc 50g, mang đến độ chính xác tuyệt đối, phản ứng tức thì cho mọi pha xử lý. Đặc biệt, ROG Strix Impact III Wireless tích hợp các công tắc micro ROG độc quyền, có tuổi thọ lên tới 70 triệu lượt nhấn, đảm bảo độ bền bỉ đáng kinh ngạc. Thiết kế socket công tắc đẩy-kéo thế hệ thứ hai cho phép người dùng dễ dàng tùy chỉnh hoặc thay thế công tắc, cá nhân hóa cảm giác nhấn theo ý muốn. Thời lượng pin ấn tượng cho phép người dùng đắm chìm vào thế giới game mà không lo lắng về việc sạc. Thiết kế đối xứng, công thái học cùng hệ thống đèn Aura Sync RGB càng làm tăng thêm vẻ đẹp và phong cách cho không gian gaming của bạn, biến mỗi trận đấu thành một trải nghiệm đỉnh cao.',
        1, 'https://product.hstatic.net/200000722513/product/impact-iii-wireless-1_b43da959481841769eb1899d9ce1e32e_1024x1024_thumb_56af8e8918cb40a48d3b2c92ec9e0139.jpg'
    );
