INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Rappo M500 Black', 'chuot-rappo-m500-black', 29,
        '{"Thương hiệu": "Rappo", "Bảo hành": "12 tháng", "Chuẩn kết nối": "Wireless 2.4Ghz / Bluetooth", "Độ phân giải": "1600 DPI"}',
        'Nâng tầm trải nghiệm làm việc và giải trí của bạn với Chuột Không Dây Rappo M500 Black – sự kết hợp hoàn hảo giữa hiệu suất vượt trội, sự thoải mái tối đa và phong cách tinh tế. Với thiết kế công thái học thông minh, Rappo M500 được chế tạo để ôm trọn lòng bàn tay, mang lại cảm giác cầm nắm tự nhiên và dễ chịu, giảm thiểu mệt mỏi ngay cả khi sử dụng trong thời gian dài.\n\nSử dụng công nghệ không dây 2.4GHz tiên tiến, chiếc chuột này đảm bảo kết nối ổn định và đáng tin cậy với phạm vi hoạt động lên đến 10 mét, giúp bạn tự do di chuyển và loại bỏ hoàn toàn sự vướng víu của dây cáp. Độ phân giải quang học chính xác cùng khả năng điều chỉnh DPI linh hoạt cho phép con trỏ di chuyển mượt mà, phản hồi nhanh chóng và chính xác trên mọi bề mặt, phục vụ hoàn hảo cho mọi tác vụ từ lướt web, chỉnh sửa tài liệu cho đến thiết kế đồ họa.\n\nMàu đen sang trọng, lịch lãm của Rappo M500 không chỉ làm nổi bật không gian làm việc của bạn mà còn thể hiện sự chuyên nghiệp. Với các nút bấm bền bỉ, có tuổi thọ cao, chiếc chuột này cam kết đồng hành cùng bạn qua hàng triệu lần nhấp. Tính năng \"plug and play\" đơn giản cho phép bạn kết nối và sử dụng ngay lập tức mà không cần cài đặt phức tạp, cùng với công nghệ tiết kiệm pin thông minh giúp kéo dài thời gian sử dụng. Chuột Rappo M500 Black chính là lựa chọn lý tưởng cho những ai tìm kiếm một thiết bị ngoại vi đáng tin cậy, thoải mái và đầy phong cách.',
        1, 'https://product.hstatic.net/200000722513/product/rapoo_m500_5045a0fd08074d0ca80e2450a884ff39.jpg'
    );
