INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột E-DRA EM623W Không dây RGB Đen', 'chuot-e-dra-em623w-khong-day-rgb-den', 23,
        '{"Nhà Sản Xuất": "E-Dra", "Bảo Hành": "24 tháng", "Tình Trạng": "Mới 100%", "Kết nối": "USB Type C / Wireless 2.4 / Bluetooth"}',
        'Chuột gaming E-DRA EM623W này không chỉ đẹp mắt mà còn rất thuận tiện cho việc sử dụng. Với thiết kế đối xứng và viền mượt mà, nó ôm sát hoàn hảo vào lòng bàn tay của bạn, mang lại cảm giác thoải mái và tự nhiên trong mọi tình huống sử dụng. Với trọng lượng siêu nhẹ chỉ 90g, bạn có thể di chuyển một cách linh hoạt mà không gặp bất kỳ cản trở nào.\n\n\n\nKết nối tiện lợi\nHệ thống kết nối không dây tiên tiến với cả 2.4Ghz và Bluetooth đảm bảo rằng bạn sẽ luôn được kết nối ổn định mà không cần lo lắng về sự gián đoạn hoặc trễ lag. Điều này mang lại sự linh hoạt tuyệt vời cho cả người chơi game chuyên nghiệp và những người làm việc cần tính di động. Còn ấn tượng với độ phân giải cao lên đến 3200 DPI, đảm bảo mọi chuyển động được ghi nhận chính xác và mượt mà. Chuột E-DRA EM623W Không dây RGB Đen là một công cụ lý tưởng không chỉ cho việc làm việc hàng ngày mà còn cho trải nghiệm chơi game vô cùng tốt.\n\n\n\n \n\nHệ thống đèn LED đỉnh cao\nSwitch Omron chất lượng cao, bạn có thể tin tưởng vào độ bền của sản phẩm với tuổi thọ lên đến 20 triệu lần nhấn. Có thể sử dụng chiếc chuột này một cách thoải mái trong thời gian dài mà không cần lo lắng về việc thay thế. Hệ thống LED RGB 16 triệu màu, cho phép bạn tùy chỉnh hiệu ứng ánh sáng theo sở thích cá nhân, tạo điểm nhấn và phong cách riêng cho không gian làm việc của bạn.\n\n\n\nThêm vào đó, E-DRA Không dây EM625X Superlight White và E-DRA Không dây EM625X Superlight Black mang đến trải nghiệm di chuột mượt mà, phản hồi nhanh và độ chính xác cao trong từng thao tác.',
        1, 'https://product.hstatic.net/200000722513/product/50655_chuot_gaming_e_dra_em623w_5_eeecd2420cc949f7bbd299ecfd50132c.jpg'
    );
