INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Bluetooth Silent Rapoo M650 Lucky Cat', 'chuot-bluetooth-silent-rapoo-m650-lucky-cat', 29,
        '{"Tên sản phẩm":"Chuột không dây Bluetooth Rapoo M650 Silent","Tương thích":"Windows, MacOS","Độ phân giải":"1300 DPI","Nút nhấn":"3 nút","Kết nối":"Bluetooth 3.0/5.0, 2.4GHz","Khoảng cách kết nối":"10 m","Đèn LED":"Không","Pin":"1 pin AA","Thời gian sử dụng":"Lên đến 9 tháng","Tiện ích":"Nút click êm, không gây tiếng ồn, kết nối tối đa 3 thiết bị","Kích thước":"10.01 x 6.37 x 2.65 cm","Hãng sản xuất":"Rapoo"}',
        'Chuột không dây Silent Rapoo M650 kết nối không dây qua USB Receiver 2.4GHz hoặc Bluetooth 3.0, 5.0. Bạn có thể dùng chuột không dây cùng với các thiết bị như iPad, Notebook, Macbook, PC, iMac qua kết nối tương thích tại bất kỳ đâu cho nhu cầu xử lý công việc hay giải trí của mình.\n\nVới khoảng cách kết nối đến 10 mét, thật dễ dàng để sử dụng chuột trong mọi không gian.\n\nKết nối đồng thời với 3 thiết bị, luân phiên làm việc dễ dàng giữa chúng, cho không gian làm việc thêm gọn gàng, thao tác xử lý thêm thuận tiện. Chuột Rapoo M650 sử dụng pin AA thông dụng, dễ thay thế. Tích hợp công nghệ tiết kiệm pin Rapoo’s energy saving và công tắc bật nguồn cho phép kéo dài thời gian sử dụng pin đến 9 tháng, yên tâm sử dụng với tần suất cao trong thời gian dài.\n\nChuột Rapoo M650 tương thích tốt với hệ điều hành Windows, MacOS (MacBook, iMac) thông dụng.\n\nDi chuyển nhanh, nhạy với thao tác chuẩn xác nhờ độ phân giải tối đa đến 1300 DPI. Với mức DPI độ phân giải cao 1.300 dpi của M650 Silent phù hợp với nhu cầu làm việc, thiết kế cơ bản hoặc giải trí.\n\nChuột Rapoo M650 không dây Silent không tiếng ồn, êm ái trong mọi thao tác. Như vậy, chỉ với một mức đầu tư không lớn cho chuột máy tính thông minh, tiện ích, bạn sẽ thực sự hài lòng với những trải nghiệm cùng chuột không dây Rapoo M650 Silent.',
        1, 'https://product.hstatic.net/200000722513/product/gearvn-chuot-bluetooth-silent-rapoo-m650-lucky-cat_f49d7f66053f42988c3107473d5b0b28.jpg'
    );
