INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech G102 LightSync Black', 'chuot-logitech-g102-lightsync-rgb-black', 13,
        '{"Tên sản phẩm": "Chuột Logitech G102 LightSync Black", "Thương hiệu": "Logitech", "Màu sắc": "Đen", "Loại chuột": "Chuột quang có dây", "Độ phân giải": "200 - 8000 DPI", "Tốc độ phản hồi": "1000Hz (1ms)", "Số nút bấm": "6 nút lập trình được", "Đèn LED RGB": "Có, LightSync RGB 8-zone", "Cảm biến": "HERO 2K", "Trọng lượng": "85g (chỉ chuột)", "Kích thước": "116.6mm x 62.15mm x 38.2mm", "Chiều dài dây": "2.1m", "Hệ điều hành tương thích": "Windows 7 trở lên, macOS 10.11 trở lên, Chrome OS", "Phần mềm điều khiển": "Logitech G HUB", "Tính năng đặc biệt": "Nút chuyển đổi DPI nhanh, Tích hợp bộ nhớ lưu profile", "Chất liệu": "Nhựa ABS", "Kiểu dáng": "Đối xứng, phù hợp cho cả người thuận tay trái và tay phải"}',
        'Chuột Logitech G102 Lightsync Black là một lựa chọn tuyệt vời dành cho game thủ và người dùng thông thường tìm kiếm sự kết hợp hoàn hảo giữa hiệu suất, độ bền và phong cách. Với thiết kế cổ điển, công thái học, G102 mang lại cảm giác cầm nắm thoải mái cho cả người thuận tay trái và tay phải, đảm bảo bạn có thể chơi game hoặc làm việc hàng giờ liền mà không cảm thấy mệt mỏi. Điểm nhấn của G102 chính là công nghệ chiếu sáng Lightsync RGB tiên tiến, cho phép tùy chỉnh 16,8 triệu màu sắc và các hiệu ứng ánh sáng động thông qua phần mềm Logitech G HUB. Bạn có thể đồng bộ hóa ánh sáng với các thiết bị Logitech G khác, tạo nên một không gian chơi game đầy màu sắc và cá tính. Bên trong, G102 được trang bị cảm biến chơi game có độ chính xác cao, cung cấp dải DPI rộng từ 200 đến 8000, có thể điều chỉnh nhanh chóng chỉ với một nút bấm. Điều này đảm bảo mỗi chuyển động của bạn đều được ghi nhận một cách mượt mà và chính xác, từ những pha xử lý nhanh trong game FPS đến những thao tác tỉ mỉ trong công việc đồ họa. Với 6 nút có thể lập trình, người dùng dễ dàng gán các macro phức tạp hoặc phím tắt yêu thích, tối ưu hóa trải nghiệm sử dụng. Tốc độ phản hồi 1ms (1000Hz) đảm bảo mọi lệnh của bạn được thực hiện gần như ngay lập tức. Đây thực sự là một con chuột đáng giá cho mọi nhu cầu.',
        1, 'https://product.hstatic.net/200000722513/product/logitech-g102-lightsync-rgb-black-1_bf4f5774229c4a0f81b8e8a2feebe4d8_aeb4ae49ee844c3e9d315883d4e482d4.jpg'
    );
