INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer DeathAdder Essential (RZ01-03850100-R3M1)', 'razer-deathadder-essential', 15,
        '{"Tên sản phẩm": "Razer DeathAdder Essential", "Mã sản phẩm": "RZ01-03850100-R3M1", "Màu sắc": "Đen", "Loại chuột": "Chuột Gaming có dây", "Cảm biến": "Optical", "Độ phân giải cảm biến tối đa": "6400 DPI", "Tốc độ theo dõi": "220 IPS", "Gia tốc tối đa": "30 G", "Số nút lập trình": "5", "Tuổi thọ nút bấm": "10 triệu lượt nhấn", "Đèn LED": "Đèn LED xanh lá đơn sắc", "Tần số báo cáo USB": "1000 Hz", "Kích thước": "127.0 mm (dài) x 61.7 mm (rộng) x 42.7 mm (cao)", "Trọng lượng": "96g (không bao gồm cáp)", "Chiều dài cáp": "1.8m", "Tương thích hệ điều hành": "Windows 7 trở lên", "Phần mềm đi kèm": "Razer Synapse 3"}',
        'Chuột Razer DeathAdder Essential (RZ01-03850100-R3M1) là biểu tượng của sự đáng tin cậy và hiệu suất vượt trội trong thế giới gaming. Được thiết kế với hình dáng công thái học đã được khẳng định qua hàng triệu người dùng trên toàn cầu, chiếc chuột này mang lại sự thoải mái tối đa cho những phiên chơi game kéo dài, giảm thiểu mệt mỏi và tăng cường khả năng kiểm soát. Với cảm biến quang học 6.400 DPI chính xác, DeathAdder Essential đảm bảo độ nhạy tuyệt đối và khả năng phản hồi tức thì, cho phép bạn thực hiện những pha di chuyển mượt mà và nhắm mục tiêu chuẩn xác ngay cả trong những trận chiến căng thẳng nhất. Năm nút Hyperesponse có thể lập trình độc lập, với tuổi thọ lên đến 10 triệu lượt nhấp, cung cấp độ bền vượt trội và khả năng tùy chỉnh linh hoạt để phù hợp với mọi phong cách chơi game. Thiết kế đơn giản nhưng mạnh mẽ, lớp vỏ chắc chắn và chất lượng hoàn thiện cao cấp là minh chứng cho tiêu chuẩn kỹ thuật hàng đầu của Razer. DeathAdder Essential không chỉ là một công cụ chơi game mà còn là một người bạn đồng hành đáng tin cậy, giúp bạn nâng cao hiệu suất và thống trị mọi chiến trường ảo, từ những tựa game bắn súng tốc độ cao đến các trò chơi chiến thuật phức tạp. Đây là lựa chọn lý tưởng cho game thủ đang tìm kiếm sự kết hợp hoàn hảo giữa hiệu suất, độ bền và mức giá phải chăng.',
        1, 'https://product.hstatic.net/200000722513/product/thumbchuot_7445abea69bf461e881eeba2b6cbbd8d.jpg'
    );
