INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech G304 Wireless', 'chuot-logitech-g304-lightspeed-wireless', 13,
        '{"Tên sản phẩm": "Chuột Logitech G304 Wireless", "Công nghệ kết nối": "Không dây LIGHTSPEED", "Công nghệ cảm biến": "HERO", "Độ phân giải": "200 – 12.000 DPI", "Tốc độ báo cáo không dây": "1000 Hz", "Bộ xử lý": "ARM 32-bit", "Micro-bộ nhớ": "Tích hợp 5 cấu hình", "Tích hợp bộ đệm nút": "Có", "Tuổi thọ pin": "Lên đến 250 giờ", "Yêu cầu hệ thống": "Windows 10 trở lên, macOS 10.13 trở lên, Chrome OS, Android TV", "Trọng lượng": "99g", "Màu sắc": "Đen"}',
        'Chuột gaming không dây Logitech G304 Lightspeed là sản phẩm đang làm mưa làm gió trên thị trường với nhiều tính năng đặc biệt nổi bật. Với nhiều công phá mới trong thiết kế cùng mức giá thành tương đối rẻ sản phẩm chuột Logitech đã sẵn sàng để mọi người dùng trải nghiệm.\n\nThiết kế đột phá, nổi bật với nhiều nút lập trình\nChuột gaming không dây Logitech G304 Lightspeed được nhà sản xuất gia công vô cùng tỉ mỉ với tiêu chí là đặt nhu cầu của người sử dụng hàng đầu. Chuột sở hữu 6 nút lập trình chính giữa và ở cả 2 bên. Các nút kích chuột chính vô cùng bền bỉ với khả năng 10 triệu lần nhấp chuột.\n\nĐánh giá chuột gaming không dây Logitech G304 Lightspeed\n\nBên cạnh đó, hệ thống hút lực cho các nút cơ học của chuột không dây G304 hoàn toàn tách biệt giúp nâng cao được sự ổn định của các nút phải và trái và giảm tối đa lực tác dụng khi sử dụng chuột. Điều đó mang lại cho người dùng những thao tác nhanh chóng ngay cả trong những trận game căng thẳng.\n\nThời lượng pin lớn, mắt cảm biến Hero\nChuột gaming không dây Logitech G304 Lightspeed là một trong những chiếc chuột không dây mang đến thời lượng pin sử dụng vượt trội so với các sản phẩm cùng phân khúc. Chuột mang lại cho người dùng 250h sử dụng chỉ với một chiếc pin AA.\n\nThời lượng pin lớn, mắt cảm biến Hero\n\nNgoài ra, chuột còn sở hữu công nghệ cảm biến Hero có khả năng mang đến các thao tác click chuột mượt mà, tăng tốc với hiệu suất đỉnh cao nhưng vẫn có thể tiết kiệm năng lượng tối ưu.',
        1, 'https://product.hstatic.net/200000722513/product/gvn_log_g304_3df28cd60a48412b8fb1d2ff762dc6a9_1f12340f2e6b4b8892163de0a06676f2.png'
    );
