INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech G304 Wireless White', 'chuot-logitech-g304-lightspeed-wireless-white', 13,
        '{"Kích thước": "116.6 x 62.15 x 38.2 (mm) (Dài x Rộng x Cao)", "Trọng lượng": "99g", "Cảm biến": "HERO", "Độ phân giải": "200 – 12000 DPI", "Tăng tốc tối đa": ">40G", "Tốc độ tối đa": ">400 IPS", "Tốc độ báo cáo không dây": "1000Hz (1ms)", "Công nghệ LIGHTSPEED": "Có", "Bộ vi xử lý": "ARM 32-bit", "Tuổi thọ pin": "250 giờ", "Bộ nhớ tích hợp": "1 cấu hình", "Hệ thống": "Ứng lực nút cơ học", "Tương thích phần mềm": "Logitech Gaming Software"}',
        'Chuột Logitech G304 Wireless White là biểu tượng của sự tự do và hiệu suất trong thế giới gaming. Được trang bị công nghệ không dây LIGHTSPEED tiên tiến độc quyền của Logitech, G304 mang lại trải nghiệm chơi game mượt mà, ổn định với độ trễ chỉ 1ms, không thua kém bất kỳ mẫu chuột có dây nào.\n\nTrái tim của G304 là cảm biến HERO đột phá, cung cấp độ chính xác vượt trội và hiệu quả năng lượng đáng kinh ngạc. Với dải DPI từ 200 đến 12.000, cảm biến HERO đảm bảo mọi chuyển động của bạn đều được ghi nhận một cách hoàn hảo, dù là những pha flick nhanh hay những cú nhắm bắn tỉ mỉ.\n\nMột trong những điểm nhấn ấn tượng nhất của G304 là thời lượng pin cực kỳ dài. Chỉ với một viên pin AA, chuột có thể hoạt động liên tục lên đến 250 giờ, giúp bạn thoải mái chinh chiến mà không lo gián đoạn. Đèn báo pin sẽ thông báo khi pin còn 15%, cho phép bạn chuẩn bị thay thế kịp thời.\n\nVới trọng lượng chỉ 99 gram, G304 là một trong những chú chuột không dây nhẹ nhất trên thị trường, giảm mỏi tay đáng kể trong các phiên chơi game kéo dài. Thiết kế đối xứng, nhỏ gọn phù hợp với nhiều kiểu cầm chuột, mang lại sự thoải mái tối ưu. Màu trắng tinh tế không chỉ làm nổi bật không gian gaming của bạn mà còn thể hiện phong cách riêng biệt.\n\nSáu nút có thể lập trình thông qua phần mềm Logitech G HUB cho phép cá nhân hóa trải nghiệm của bạn, biến G304 thành một công cụ mạnh mẽ trong mọi tình huống.',
        1, 'https://product.hstatic.net/200000722513/product/h-g304-lightspeed-wireless-white-1000_e28318985b5049099c4e8381695e810f_782b9a4494994a2ea98cf9e699e35200.jpg'
    );
