INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech G502 Hero Gaming', 'logitech-g502-hero', 13,
        '{"Tương thích": "Windows® 7 trở lên, macOS 10.11 trở lên, ChromeOS", "Độ phân giải": "100 – 25.600 DPI", "Cảm biến": "Quang học HERO 25K", "Số nút": "11 nút lập trình được", "Kết nối": "Dây USB", "Chiều dài dây": "2,1 m", "Đèn LED": "LIGHTSYNC RGB", "Ứng dụng hỗ trợ": "HUB G của Logitech", "Tần suất gửi tín hiệu": "1000Hz (1ms)", "Kích thước": "29 x 7.5 x 4 cm (Cao x Dài x Rộng)", "Khối lượng": "121 g", "Hãng sản xuất": "Logitech"}',
        'Logitech nhà sản xuất phụ kiện dành cho máy tính nổi tiếng trên thế giới vừa cho ra mắt dòng phụ kiện chuột chơi Game có dây Logitech G502 Hero. Với nhiều tối ưu hóa nâng cấp đặc biệt dành cho chơi game, sản phẩm dần chiếm được sự yêu thích của nhiều người dùng.\n\nChuột chơi Game có dây Logitech G502 Hero\n\nChuột chơi Game có dây Logitech G502 Hero có thiết kế gọn nhẹ, độ nặng có thể tùy chỉnh\nSản phẩm chuột Logitech có thiết kế nguyên khối được làm bằng nhựa, trọng lượng nặng 3.6g đi kèm với Hero G502 đều có thể điều chỉnh theo nhiều cách: ở phía trước, phía sau, trái, phải và trung tâm. Từ đó người dùng có thể tìm ra vị trí phù hợp nhằm tối ưu hóa hiệu suất khi chơi game.\n\nChuột chơi Game có dây Logitech G502 Hero thiết kế gọn nhẹ, tùy chỉnh được độ nặng\n\nĐộ bền tuyệt đối trên chuột chơi Game có dây Logitech G502 Hero\nChuột chơi Game có dây Logitech G502 Hero được thiết kế đảm bảo mang lại độ bền tuyệt đối. Điển hình là các nút trái/phải với switch cơ học đảm bảo 50 triệu lần nhấp, tương đương với 10 giờ chơi liên tục mỗi ngày trong 5 năm. Ngoài ra Logitech còn trang bị lớp đệm cao su hai bên giúp tăng độ bền và cảm giác cầm nắm chắc chắn cho người dùng.\n\nChuột chơi Game có dây Logitech G502 Hero độ bền cao, bền bỉ theo thời gian\n\nHiệu ứng ánh sáng tùy chỉnh trên chuột chơi Game có dây Logitech G502 Hero\nChuột chơi Game có dây Logitech G502 Hero được cung cấp hiệu ứng ánh sáng lên đến 16,8 triệu màu sử dụng công nghệ Lightsync RGB của Logitech. Công nghệ Lightsync cung cấp các hiệu ứng chiếu sáng phản ứng theo hành động trong game, thể hiện âm thanh bằng màu sắc, tạo mẫu màu màn hình và nhiều hơn nữa. Người dùng cũng có thể tự tùy chỉnh màu sắc để thể hiện cá nhân hóa sở thích mỗi người.\n\nChuột chơi Game có dây Logitech G502 Hero có thể tùy chỉnh màu sắc RGBs\n\nTích hợp cảm biến hero 16K trên chuột chơi Game có dây Logitech G502 Hero\nHero là cảm biến chơi game chính xác nhất từ trước đến nay do Logitech sản xuất. Đặc biệt độ chính xác của cảm biến ở Chuột chơi Game có dây Logitech G502 Hero đã được nâng cấp toàn diện. Với khả năng xử lý khung hình nhanh nhất và tạo ra 400+ IPS trong dải DPI 100 – 16.000 với khả năng làm mịn, lọc và tăng tốc. Từ đó cảm biến Hero đạt được sự chính xác cấp độ thi đấu và độ nhạy ổn định nhất từ trước tới nay.\n\nChuột chơi Game có dây Logitech G502 Hero tích hợp cảm biến Hero 16K mới nhất\n\nKhả năng điều khiển trên đầu ngón tay ở chuột chơi Game có dây Logitech G502 Hero\nChuột chơi Game có dây Logitech G502 Hero cung cấp cho người dùng 11 nút có thể lập trình trong quá trình sử dụng chuột. Người dùng có thể thiết kế, di chuyển, sửa chữa…hay để sức mạnh di chuyển dưới ngón tay và lưu đến 5 cấu hình khác nhau trực tiếp vào chuột để mang cài đặt đi khắp mọi nơi.',
        1, 'https://product.hstatic.net/200000722513/product/10001_01736316d2b443d0838e5a0741434420.png'
    );
