INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột DareU EM901X RGB Superlight Wireless Pink', 'chuot-dareu-em901x-rgb-superlight-wireless-pink', 25,
        '{"Hãng sản xuất": "Dare-U", "Bảo hành": "24 tháng", "Model": "DareU EM901X RGB Superlight Wireless Pink", "Màu sắc": "Hồng", "Thiết kế": "Đối xứng", "Kiểu kết nối": "Wireless 2.4Ghz", "Thời gian sử dụng": "30 giờ (tắt LED RGB) / 18 giờ (bật LED RGB)", "Thời gian sạc": "3 giờ", "Dung lượng pin": "930 mAh", "Đèn LED": "RGB", "Độ nhạy (DPI)": "800-6000", "Polling rate": "1000Hz", "Tracking": "150 IPS", "Cảm biến": "BRAVO (ATG4090)", "Số nút bấm": "6", "Switch": "DareU (10 triệu lần click)", "Kích thước": "125.5 x 68.6 x 39.6 mm", "Trọng lượng": "86g"}',
        'Chuột DareU EM901X RGB Superlight Wireless Pink là sự kết hợp hoàn hảo giữa phong cách, hiệu suất và sự tiện lợi, dành riêng cho những ai yêu thích công nghệ và thẩm mỹ. Với tông màu hồng pastel ngọt ngào, chiếc chuột này không chỉ là một công cụ làm việc hay giải trí mà còn là một phụ kiện thời trang nổi bật trên bàn làm việc của bạn. Điểm nhấn đầu tiên chính là thiết kế siêu nhẹ, chỉ với trọng lượng tối ưu, EM901X giúp người dùng thao tác nhanh chóng và thoải mái trong nhiều giờ liền mà không gây mỏi cổ tay, lý tưởng cho các buổi chơi game marathon hay làm việc cường độ cao. Công nghệ không dây 2.4GHz tiên tiến đảm bảo kết nối ổn định, mượt mà, loại bỏ hoàn toàn sự vướng víu của dây cáp truyền thống, mang lại trải nghiệm tự do di chuyển tối đa. Bên cạnh đó, hệ thống đèn nền RGB rực rỡ có thể tùy chỉnh dễ dàng, cho phép bạn cá nhân hóa không gian làm việc hoặc giải trí theo sở thích, tạo nên hiệu ứng thị giác ấn tượng. Được trang bị cảm biến quang học hiệu suất cao, DareU EM901X mang lại độ chính xác vượt trội, đáp ứng hoàn hảo mọi thao tác từ di chuyển con trỏ thông thường đến những pha xử lý game phức tạp, đòi hỏi tốc độ và độ nhạy cao. Thời lượng pin ấn tượng đảm bảo bạn có thể sử dụng liên tục trong thời gian dài, và khả năng sạc nhanh giúp thiết bị luôn sẵn sàng. Các nút bấm được thiết kế bền bỉ, mang lại cảm giác nhấn rõ ràng và tuổi thọ cao. Đây thực sự là lựa chọn tuyệt vời cho các game thủ nữ, dân văn phòng hoặc bất kỳ ai tìm kiếm một chiếc chuột không dây siêu nhẹ, đẹp mắt và hiệu quả.',
        1, 'https://product.hstatic.net/200000722513/product/khong-day-gaming-dareu-em901x-pink-01_542ff31224024dd3813ed701f4926555_02f0e97ca40347fabb5ca635370597a5.jpg'
    );
