INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Deathadder Essential White', 'chuot-razer-deathadder-essential-white', 15,
        '{"Tên sản phẩm": "Razer Deathadder Essential", "Màu sắc": "Trắng", "Loại chuột": "Có dây", "Cảm biến": "Optical", "Độ phân giải DPI": "6400", "Tốc độ theo dõi": "220 IPS", "Gia tốc tối đa": "30 G", "Số nút lập trình": "5", "Tuổi thọ nút": "10 triệu lượt nhấn", "Đèn LED": "Đơn sắc xanh lá", "Polling Rate": "1000 Hz", "Kết nối": "USB", "Chiều dài dây cáp": "1.8m", "Kích thước (Dài x Rộng x Cao)": "117mm x 64mm x 43mm", "Trọng lượng": "96g", "Yêu cầu hệ thống": "Windows 7 trở lên"}',
        'Chuột gaming Razer DeathAdder Essential – Hiệu năng ổn định, thiết kế công thái học đỉnh cao\n\nRazer DeathAdder Essential được thiết kế với kiểu dáng công thái học (Ergonomic) đặc trưng của Razer, mang lại cảm giác thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Thiết kế cổ điển nhưng vẫn đầy tinh tế và mạnh mẽ giúp người chơi luôn duy trì hiệu suất cao trong mọi trận đấu. Đây chắc chắn là một trong những mẫu chuột gaming dưới 1 triệu đáng trải nghiệm nhất hiện nay.\n\nLà phiên bản thu gọn của dòng DeathAdder huyền thoại, DeathAdder Essential có trọng lượng nhẹ chỉ khoảng 69g, dù vẫn giữ kích thước khá lớn và thân chuột hơi bè sang hai bên. Bề mặt chuột được phủ lớp nhám cao cấp, kết hợp với hai phiên bản màu đen (LED xanh) và trắng (LED trắng) cùng logo ba chú rắn đặc trưng của Razer, mang đến vẻ ngoài đậm chất gaming và sang trọng.\n\nThiết kế công thái học – Cầm nắm thoải mái, thao tác linh hoạt\n\nChuột vẫn giữ form cầm công thái học truyền thống của dòng DeathAdder, mang lại cảm giác ôm tay cực kỳ dễ chịu và phù hợp với hầu hết các kiểu cầm (palm, claw, fingertip). Hai bên hông được trang bị lớp nhựa chấm bi chống trượt, giúp tăng độ bám và tạo cảm giác chắc chắn hơn khi sử dụng. Phần đầu và đuôi chuột được làm hơi bè ra, trong khi phần giữa thon gọn và nhô cao vừa vặn ôm sát lòng bàn tay – thiết kế đặc trưng giúp game thủ thao tác nhanh và chính xác hơn.\n\nHiệu năng mạnh mẽ – Cảm biến quang học chính xác\n\nRazer DeathAdder Essential được trang bị cảm biến quang học Razer Optical 6.400 DPI, cho khả năng theo dõi chính xác và mượt mà. Tốc độ phản hồi lên đến 220 IPS và gia tốc 30G, đảm bảo mọi thao tác di chuột đều được ghi nhận tức thời. Đây là lựa chọn hoàn hảo cho những game thủ cần độ chính xác cao trong từng pha xử lý.\n\nNút bấm bền bỉ – Tùy chỉnh linh hoạt\n\nChuột có 5 nút chức năng gồm: 2 nút chính (trái – phải), nút cuộn và 2 nút bên hông trái. Tất cả đều sử dụng công nghệ Razer Mechanical Switch với độ bền lên đến 10 triệu lần nhấn, cho cảm giác nhấn chắc chắn và phản hồi cực nhanh. Các nút này đều có thể tùy chỉnh chức năng thông qua phần mềm Razer Synapse, giúp bạn dễ dàng thiết lập theo thói quen hoặc nhu cầu chơi game cá nhân.\n\nTích hợp hệ sinh thái Razer Synapse\n\nDeathAdder Essential tương thích hoàn hảo với phần mềm Razer Synapse, cho phép tùy chỉnh DPI, cấu hình nút, hiệu ứng đèn LED và lưu cấu hình trực tiếp vào tài khoản Razer. Nhờ vậy, bạn có thể mang theo thiết lập yêu thích của mình ở bất cứ đâu.',
        1, 'https://product.hstatic.net/200000722513/product/565656_22914bb589c146e599cb381f2c75b557_e6b08a36816248339bcf29ca71560fcb.png'
    );
