INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech Pebble Mouse 2 M350S Graphite', 'chuot-logitech-pebble-mouse-2-m350s-graphite', 13,
        '{"Thương hiệu": "Logitech", "Model": "Logitech M350s", "Bảo hành": "12 tháng", "Loại chuột": "Văn phòng", "Màu sắc": "Đen", "Kết nối": "Công nghệ Bluetooth tiết kiệm năng lượng", "Phạm vi không dây": "Lên đến 10m", "Pin": "1 pin AA lắp sẵn", "Số nút": "3", "Cảm biến": "Theo dõi quang học có độ phân giải cao", "DPI": "1000", "Trọng lượng (kèm pin)": "76 gram", "Kích thước": "Chiều cao: 106,7 mm, Chiều rộng: 58,7 mm, Chiều dày: 26,62 mm"}',
        'Chuột Logitech Pebble Mouse 2 M350S Graphite là sự kết hợp hoàn hảo giữa phong cách, sự tiện lợi và hiệu suất đáng tin cậy, được thiết kế dành riêng cho những ai yêu thích sự tối giản nhưng không muốn thỏa hiệp về chất lượng. Với tông màu Graphite tinh tế và hiện đại, M350S không chỉ là một công cụ làm việc mà còn là phụ kiện thời trang trên bàn làm việc của bạn.\n\nĐiểm nổi bật đầu tiên của Pebble Mouse 2 M350S chính là thiết kế siêu mỏng và gọn nhẹ, mô phỏng hình dáng viên sỏi (pebble) tự nhiên, mang lại cảm giác cầm nắm thoải mái và dễ chịu dù sử dụng trong thời gian dài. Kích thước nhỏ gọn giúp bạn dễ dàng bỏ vào túi xách hay balo, lý tưởng cho việc di chuyển.\n\nCông nghệ SilentTouch độc quyền của Logitech đảm bảo các cú nhấp chuột và cuộn bánh xe gần như không gây ra tiếng ồn, tạo môi trường làm việc yên tĩnh, lý tưởng cho thư viện, quán cà phê hay văn phòng chia sẻ.\n\nChuột kết nối dễ dàng qua Bluetooth Low Energy, tương thích rộng rãi với hầu hết các hệ điều hành phổ biến như Windows, macOS, Chrome OS, Linux, iPadOS và Android. Khả năng kết nối đa thiết bị cho phép bạn chuyển đổi nhanh chóng giữa ba thiết bị khác nhau chỉ với một nút bấm.\n\nVới thời lượng pin lên đến 24 tháng chỉ với một viên pin AA, bạn có thể yên tâm sử dụng mà không lo lắng về việc thay pin thường xuyên. M350S cũng được làm từ vật liệu tái chế, góp phần bảo vệ môi trường.\n\nNâng cấp trải nghiệm làm việc của bạn với sự tinh tế và hiệu quả của Logitech Pebble Mouse 2 M350S Graphite.',
        1, 'https://product.hstatic.net/200000722513/product/gearvn-chuot-logitech-pebble-mouse-2-m350s-graphite-1_4b4473ddaba149268abe5a5582eb3b3b.jpg'
    );
