INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột không dây DareU LM115G Black', 'chuot-dare-u-lm115g-wireless', 25,
        '{"Hãng sản xuất": "Dareu", "Model": "LM115G", "Loại": "Chuột Wireless", "Kích thước": "107.5 x 59.15 x 38.29 (mm)", "Cảm biến": "PAW3512", "Sóng": "2.4GHz", "DPI": "800 / 1200 / 1600", "Switch": "Silent 3 triệu lượt nhấn", "Trọng lượng": "90g", "Số nút": 6}',
        'Chuột không dây DareU LM115G Black là một lựa chọn lý tưởng cho người dùng văn phòng và các tác vụ hàng ngày, mang lại sự tiện lợi và hiệu quả tối ưu. Với thiết kế công thái học màu đen sang trọng, chuột vừa vặn hoàn hảo trong lòng bàn tay, giảm thiểu mỏi cổ tay ngay cả khi sử dụng trong thời gian dài. Công nghệ kết nối không dây 2.4GHz tiên tiến đảm bảo đường truyền ổn định và mượt mà, loại bỏ hoàn toàn sự rắc rối của dây cáp. Người dùng chỉ cần cắm đầu thu USB vào máy tính là có thể bắt đầu sử dụng ngay lập tức, không cần cài đặt phức tạp. Chuột DareU LM115G Black được trang bị cảm biến quang học chất lượng cao, cung cấp độ chính xác tuyệt vời trên nhiều bề mặt khác nhau, với độ phân giải DPI có thể điều chỉnh để phù hợp với từng nhu cầu sử dụng, từ công việc văn phòng đòi hỏi sự tỉ mỉ đến lướt web thông thường. Nút bấm nhạy và con lăn mượt mà giúp thao tác nhanh chóng và hiệu quả. Ngoài ra, sản phẩm còn nổi bật với khả năng tiết kiệm pin vượt trội, kéo dài thời gian sử dụng mà không cần thay pin thường xuyên, đảm bảo công việc không bị gián đoạn. Đây là phụ kiện hoàn hảo cho bất kỳ bộ máy tính nào, mang lại trải nghiệm làm việc thoải mái và không giới hạn.',
        1, 'https://product.hstatic.net/200000722513/product/thumbchuot_615d44df818a4380b09d876790c5246a_fb6dd322d3f84957b62e4cccc36fd97d.png'
