INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech MX Anywhere 3S Graphite', 'chuot-logitech-mx-anywhere-3s-graphite', 13,
        '{"Loại chuột": "Văn phòng", "Màu sắc": "Than chì", "Trọng lượng": "99g", "Kích thước": "10.05 x 6.5 x 3.44 cm", "Công nghệ cảm biến": "Cảm biến Darkfield", "DPI": "200 - 8000 DPI", "Nút": "6 nút", "Khoảng cách không dây": "10m", "Kết nối": "Bluetooth", "Pin": "500 mAh có thể sạc (qua cáp USB-C đi kèm)", "Phần mềm": "Logi Options và Logitech Flow"}',
        'Chuột Logitech MX Anywhere 3S Graphite là người bạn đồng hành lý tưởng cho những ai thường xuyên di chuyển và làm việc ở nhiều môi trường khác nhau. Với thiết kế nhỏ gọn, tinh tế và màu than chì sang trọng, MX Anywhere 3S dễ dàng bỏ vào túi xách hoặc ba lô, sẵn sàng làm việc mọi lúc mọi nơi.\n\nĐiểm nổi bật nhất của sản phẩm này là công nghệ cuộn MagSpeed Electromagnetic, cho phép bạn cuộn qua 1.000 dòng chỉ trong một giây với độ chính xác tuyệt vời, hoặc chuyển sang chế độ cuộn từng nấc êm ái, yên tĩnh đáng kinh ngạc. Các nút bấm Quiet Click mang lại trải nghiệm làm việc tập trung hơn mà không gây tiếng ồn khó chịu.\n\nCông nghệ cảm biến Darkfield High Precision 8K DPI cho phép chuột hoạt động mượt mà trên hầu hết mọi bề mặt, kể cả mặt kính dày tối thiểu 4mm, một tính năng vượt trội mà ít chuột nào có được.\n\nBạn có thể kết nối MX Anywhere 3S qua đầu thu Logi Bolt USB siêu bảo mật hoặc Bluetooth Low Energy, tương thích với nhiều hệ điều hành như Windows, macOS, Linux, ChromeOS, iPadOS và Android. Khả năng kết nối và chuyển đổi nhanh chóng giữa ba thiết bị giúp tối ưu hóa quy trình làm việc đa nhiệm.\n\nPin sạc USB-C có thể sử dụng tới 70 ngày chỉ với một lần sạc đầy, và chỉ cần sạc nhanh 1 phút là có thêm 3 giờ sử dụng. Tùy chỉnh các nút chức năng qua phần mềm Logi Options+ để phù hợp với phong cách làm việc của bạn.\n\nMX Anywhere 3S Graphite thực sự là một công cụ mạnh mẽ, linh hoạt và tiện lợi cho người dùng chuyên nghiệp.',
        1, 'https://product.hstatic.net/200000722513/product/mx-anywhere-3s-mouse-top-view-graphite_c766ef28d410408c9a24442cf7ab7940.png'
    );
