INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Cobra', 'chuot-razer-cobra', 15,
        '{"Hãng sản xuất": "Razer", "Bảo hành": "24 Tháng", "Model": "Razer Cobra", "Màu sắc": "Đen", "Kiểu kết nối": "Có dây", "Độ nhạy (DPI)": "8500", "Cảm biến": "8500 DPI Optical Sensor", "Số nút bấm": "90 Triệu lần nhấn", "Switch": "Optical Mouse Switches Gen-3", "Kích thước": "Dài: 119.6 mm / 4.71 in, Rộng: 62.5 mm / 2.46 in, Cao: 38.1 mm / 1.50 in", "Khối lượng": "58 Gram", "Phụ kiện": "Hướng dẫn thông tin sản phẩm quan trọng"}',
        'Chuột Razer Cobra là một tuyệt tác kỹ thuật được thiết kế dành cho game thủ Esports chuyên nghiệp và những người đam mê hiệu suất cao. Với trọng lượng siêu nhẹ chỉ 58g, Cobra mang lại cảm giác lướt chuột nhanh chóng, mượt mà và không gây mỏi dù là trong những trận đấu kéo dài. Thiết kế công thái học đối xứng, nhỏ gọn của nó phù hợp với nhiều kiểu cầm chuột, từ claw grip đến fingertip grip, đảm bảo sự thoải mái tối đa và khả năng kiểm soát tuyệt vời. Chuột được trang bị cảm biến quang học Razer Focus Pro 30K DPI tiên tiến nhất, mang lại độ chính xác không thể sánh bằng và khả năng theo dõi siêu nhạy, cho phép mọi cử động đều được chuyển đổi thành hành động trong game một cách hoàn hảo. Các switch quang học Razer Gen-3 thế hệ mới với tuổi thọ 90 triệu lần nhấn đảm bảo độ bền vượt trội và phản hồi tức thì với độ trễ gần như bằng không. Dây cáp Razer Speedflex mềm dẻo giảm thiểu lực cản, mang lại trải nghiệm di chuyển chuột không giới hạn. Ngoài ra, Razer Cobra còn tích hợp 6 nút có thể lập trình, cho phép người dùng tùy chỉnh macro và gán chức năng riêng biệt. Hệ thống đèn nền Razer Chroma RGB với 16.8 triệu màu tùy chỉnh, cùng với các chế độ ánh sáng động, không chỉ làm tăng tính thẩm mỹ mà còn giúp tạo không khí gaming sống động. Razer Cobra là sự kết hợp hoàn hảo giữa tốc độ, độ chính xác và sự thoải mái, là lựa chọn lý tưởng để nâng tầm trải nghiệm gaming của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/1_4944caaca4a941a7b730a314ff40516a.jpg'
    );
