INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Asus Rog Gladius III Wireless Aimpoint Black', 'chuot-asus-rog-gladius-iii-wireless-aimpoint-black', 3,
        '{"Hãng sản xuất": "Asus", "Model": "Chuột gaming không dây ROG Gladius III Wireless AIMPOINT (Màu đen), cảm biến ROG Aimpoint độc quyền 36000 DPI, switch dễ thay thế, công nghệ ROG SpeedNova, cáp Paracord", "Màu sắc": "Đen", "AURA Sync": "Có", "Cáp": "ROG Paracord", "Phần mềm": "Armoury Crate", "Kích thước": "123 mm x 68 mm x 44 mm", "Trọng lượng": "79g"}',
        'Asus Rog Gladius III Wireless Aimpoint dòng chuột không dây chơi game với phạm vi kết nối rộng, độ trễ cực thấp cùng với tốc độ phản hồi cực nhanh mang đến cho người chơi những pha xử lý mượt. Đặc biệt, với thiết kế công thái học giúp giảm mệt mỏi ngay cả sau nhiều giờ sử dụng, cùng với thời lượng pin dài giúp bạn tự tin tham gia mọi trò chơi.\n\nThiết kế công thái học thoải mái sử dụng cả ngày dài\nChuột Asus Rog Gladius III Wireless Aimpoint Black dòng chuột gaming sở hữu phối màu đen \"huyền bí\" cùng những chi tiết được gia công tỉ mỉ cùng phần logo nổi bật với tông màu đỏ tạo nên tổng thể vô cùng đẹp mắt với những đường nét mềm mại. Sở hữu thiết kế chuột công thái học giúp giảm mệt mỏi, làm giảm áp lực lên cổ tay khi sử dụng chuột trong thời gian dài.\n\nROG Micro Switches cho cảm giác nhấp chuột tuyệt vời\nĐể những pha xử lý trong game thêm phần chính xác, Asus Rog Gladius III Wireless Aimpoint được hãng trang bị công tắc ROG Micro Switches cho cảm giác nhấp chuột tuyệt vời, mượt mà trong từ pha xử lý yêu cầu tốc độ cao.\n\nĐồng thời để chuyển động mượt mà và nhanh chóng, chuột gaming từ ASUS còn sở hữu tính năng ROG Paracord và chân chuột 100% PTFE.\n\nCảm biến quang học ROG AimPoint\nĐộ chính xác hoàn hảo qua bội cảm biến quang học ROG AimPoint giúp bạn tự tin trong những pha giao tranh. Người chơi hoàn toàn có thể thay đổi chỉ số DPI một cách dễ dàng từ 100 đến 36.000 DPI.\n\nHệ thống chiếu sáng độc đáo\nDễ dàng tinh chính với nhiều chế độ chiếu sáng trong qua phần mềm đi Aura Sync RGB. Bạn hoàn toàn có thể cá nhân hóa hiệu ứng chiếu sáng tự tạo cho mình “Hệ sinh thái” gaming gear bao gồm bàn phím, tai nghe gaming và nhiều phụ kiện khác với những hiệu ứng ánh sáng RGB độc đáo, sinh động.\n\nDễ dàng kết nối với nhiều chế độ khác nhau\nDễ dàng kết nối với nhiều chế độ khác nhau với 3 chế độ kết nối khác nhau. Đặc biệt, với công nghệ độc quyền ROG SpeedNova sẽ mang đến cho bạn những trải nghiệm không dây độc đáo, riêng biệt với độ trễ thấp và giải pháp năng lượng tối ưu ở chế độ RF 2,4 GHz trên dòng chuột Asus Rog Gladius III Wireless Aimpoint Black.',
        1, 'https://product.hstatic.net/200000722513/product/thumb-tap-trung-manh-recovered32125.gif12_4539fe16bff14de4b7fa826a7f6afc9b.gif'
    );
