INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech M650 Off White', 'chuot-logitech-m650-off-white', 13,
        '{"Thương hiệu": "Logitech", "Model": "Logitech M650 Off White", "Bảo hành": "12 tháng", "Loại chuột": "Văn phòng", "Màu sắc": "Trắng", "Phần mềm tùy chọn": "Hỗ trợ bởi Logitech Options+ trên macOS 10.15 trở lên và Windows 10, 11 trở lên", "Kết nối": "Không dây / Bluetooth", "Phạm vi không dây": "10 m", "Pin": "1 x pin AA", "Nút": "5", "Cảm biến": "Quang học", "DPI": "400 - 4000", "Trọng lượng": "101.4 g", "Kích thước (H x W x D)": "108.2 mm x 61 mm x 38.8 mm"}',
        'Nâng tầm trải nghiệm làm việc của bạn với Chuột Logitech M650 Off White – một sự kết hợp hoàn hảo giữa công nghệ tiên tiến, thiết kế tiện dụng và phong cách tinh tế. Chuột M650 được trang bị công nghệ SmartWheel độc quyền của Logitech, cho phép bạn cuộn siêu tốc khi cần duyệt tài liệu dài và chuyển sang chế độ cuộn từng dòng chính xác chỉ bằng một cái chạm nhẹ, mang lại hiệu quả công việc vượt trội.\n\nĐặc biệt, với công nghệ SilentTouch, mọi cú nhấp chuột đều gần như im lặng tuyệt đối (giảm 90% tiếng ồn so với M170), giúp bạn tập trung cao độ và không làm phiền những người xung quanh, lý tưởng cho mọi không gian làm việc từ văn phòng đến quán cà phê.\n\nThiết kế công thái học được cải tiến với hình dáng ôm tay tự nhiên, vùng đệm ngón cái bằng cao su mềm mại và các cạnh cong, đảm bảo sự thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. M650 có sẵn nhiều kích cỡ, bao gồm cỡ L cho tay lớn hơn, giúp người dùng dễ dàng tìm được phiên bản phù hợp nhất với mình.\n\nVề kết nối, chuột hỗ trợ đa dạng với Bluetooth năng lượng thấp và đầu thu USB Logi Bolt, đảm bảo kết nối ổn định và an toàn trên nhiều thiết bị. Tuổi thọ pin lên đến 24 tháng chỉ với một viên pin AA, giúp bạn quên đi nỗi lo sạc pin thường xuyên.\n\nPhiên bản màu Off White mang đến vẻ đẹp thanh lịch, hiện đại, dễ dàng hòa hợp với mọi không gian làm việc, tạo điểm nhấn tinh tế cho bàn làm việc của bạn. Logitech M650 Off White không chỉ là một công cụ, mà là một người bạn đồng hành đắc lực giúp bạn làm việc hiệu quả và thoải mái hơn mỗi ngày.',
