INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Corsair Katar Pro Wireless', 'chuot-corsair-katar-pro-wireless', 5,
        '{"Thương hiệu": "Corsair", "Kết nối": "SLIPSTREAM WIRELESS, Chuột Bluetooth®", "Kích thước": "115.8 x 64.2 x 37.8mm", "Trọng lượng": "96g", "DPI tối đa": "10000", "Led": "Đơn sắc", "Phần mềm": "ICUE"}',
        'Chuột Corsair Katar Pro Wireless là lựa chọn lý tưởng cho game thủ tìm kiếm sự kết hợp hoàn hảo giữa hiệu suất, độ tin cậy và sự tự do không dây. Với thiết kế nhỏ gọn và trọng lượng siêu nhẹ chỉ 96g, Katar Pro Wireless mang lại cảm giác cầm nắm thoải mái, linh hoạt, đặc biệt phù hợp với các kiểu cầm claw-grip hoặc fingertip-grip, giúp bạn thực hiện những pha di chuyển nhanh và chính xác trong mọi trận đấu.\n\nSản phẩm trang bị công nghệ kết nối không dây kép tiên tiến: CORSAIR SLIPSTREAM WIRELESS độ trễ cực thấp cho trải nghiệm gaming mượt mà, không gián đoạn, và Bluetooth cho khả năng kết nối đa năng với các thiết bị khác hoặc khi bạn cần tiết kiệm pin. Cảm biến quang học 10.000 DPI với độ chính xác cao đảm bảo mọi cú click và di chuyển đều được ghi nhận hoàn hảo, cho phép bạn kiểm soát hoàn toàn trong mọi tình huống.\n\nTuổi thọ pin ấn tượng lên đến 135 giờ khi sử dụng chế độ Bluetooth hoặc 60 giờ với SLIPSTREAM WIRELESS (sử dụng một viên pin AA), giúp bạn thoải mái chiến game trong thời gian dài mà không lo hết pin giữa chừng. Sáu nút có thể lập trình hoàn toàn thông qua phần mềm CORSAIR iCUE, cho phép bạn tùy chỉnh macro, gán lại chức năng và điều chỉnh cài đặt DPI dễ dàng. Công tắc Omron bền bỉ với 30 triệu lượt nhấn đảm bảo độ bền vượt trội và phản hồi nhanh chóng.\n\nKatar Pro Wireless không chỉ là chuột gaming mà còn là công cụ đáng tin cậy cho mọi tác vụ hàng ngày, mang lại sự tiện lợi và hiệu suất vượt trội.',
        1, 'https://product.hstatic.net/200000722513/product/-chuot-corsair-katar-pro-wireless-666_23333dfd6ea64129b9627324097a4955_5792e89cb7374c2ea30752e1ee4961c8.png'
    );
